mapper.select:
select a.id driverid, a.`name` drivername, a.workstatus, a.lat, a.lng, a.phone, a.headportraitmin, c.plateno, concat(e.`Name`, '.', d.`name`) brandname, g.`name` modelname
from pub_driver a, pub_driver_vehicle_ref b, pub_vehicle c, pub_vehcline d, pub_vehcbrand e, le_vehcline_models_ref f, le_vehiclemodels g

where b.driverid = a.id and b.vehicleid = c.id and c.vehclineid = d.id and d.vehcBrandID = e.Id and f.vehclineid = d.Id and f.vehiclemodelsid = g.Id
and a.`Status` = 1 and b.`Status` = 1 and c.`Status` = 1 and d.`Status` = 1 and f.`Status` = 1 and g.`Status` = 1 and a.workstatus = 0


	and a.workstatus = #{driverState}


	and a.workstatus = #{selectedmodel}



	and a.lat >= ${minLat} and a.lat <= ${maxLat}




	and a.lng >= ${minLon} and a.lng <= ${maxLon}




	limit #{iDisplayStart}, #{iDisplayLength}

select count(*) 
from pub_driver a, pub_driver_vehicle_ref b, pub_vehicle c, pub_vehcline d, pub_vehcbrand e, le_vehcline_models_ref f, le_vehiclemodels g

where b.driverid = a.id and b.vehicleid = c.id and c.vehclineid = d.id and d.vehcBrandID = e.Id and f.vehclineid = d.Id and f.vehiclemodelsid = g.Id
and a.`Status` = 1 and b.`Status` = 1 and c.`Status` = 1 and d.`Status` = 1 and f.`Status` = 1 and g.`Status` = 1 and a.workstatus = 0


	and a.workstatus = #{driverState}


	and a.workstatus = #{selectedmodel}



	and a.lat >= ${minLat} and a.lat <= ${maxLat}




	and a.lng >= ${minLon} and a.lng <= ${maxLon}

select
	id, name, phone
from
	pub_driver
where 1=1
 and status = 1

		and jobstatus = #{jobstatus}



		and vehicletype = #{vehicletype}


		and ((leasescompanyid in
		(select id from le_leasescompany where tocstate = "2" and status = 1)
		and platformtype = "1")
		or platformtype = "0")



			and leasescompanyid = #{leasescompanyid}

		and platformtype = #{platformtype}



		and (name like concat('%', #{queryText}, '%') or phone like concat('%', #{queryText}, '%'))



		and boundstate = #{boundstate}


		and city = #{city}


		and belongleasecompany = #{belongLeasecompany}

select
id, jobnum
from
pub_driver
where
platformtype = #{platformtype} and status = 1

	and jobstatus = #{jobstatus}


	and leasescompanyid = #{leasescompanyid}


	and boundstate = #{boundstate}


	and vehicletype = #{vehicletype}


	and (jobnum like concat('%', #{queryText}, '%'))


	and city = #{city}


	and belongleasecompany = #{belongLeasecompany}

SELECT pd.*, pv.id vehicleid FROM pub_driver pd,pub_driver_vehicle_ref pdvr,pub_vehicle pv,pub_vehicle_scope pvs, op_vehcline_models_ref ovmr,op_vehiclemodels ov WHERE 1=1 AND pd.`Status` = 1 AND pv.`Status` = 1 AND pdvr.`status` = 1 AND ovmr.`status` = 1 AND ov.`status` = 1 AND pvs.`Status` = 1
AND pd.vehicletype = #{vehicletype} AND pdvr.driverid = pd.id AND pdvr.vehicleid = pv.id AND ovmr.vehclineid = pv.vehclineid AND ovmr.vehiclemodelsid = ov.Id AND pvs.vehicleid = pv.id AND pd.workstatus IN(#{workstatuslist})
AND pd.leasescompanyid = #{companyid}
AND pd.lng >= #{minLng} AND pd.lng <= #{maxLng} AND pd.lat >= #{minLat} AND pd.lat <= #{maxLat}
UNION ALL SELECT pd.*, pv.id vehicleid FROM pub_driver pd,pub_driver_vehicle_ref pdvr,pub_vehicle pv,pub_vehicle_scope pvs, op_vehiclemodels_vehicle_ref ovvr,op_vehiclemodels ov WHERE 1=1 AND pd.`Status` = 1 AND pv.`Status` = 1 AND pdvr.`status` = 1 AND ovvr.`status` = 1 AND ov.`status` = 1 AND pvs.`Status` = 1
AND pd.vehicletype = #{vehicletype} AND pdvr.driverid = pd.id AND pdvr.vehicleid = pv.id AND ovvr.vehicleid = pv.id AND ovvr.vehiclemodelsid = ov.Id AND pvs.vehicleid = pv.id AND pd.workstatus IN(#{workstatuslist})
AND pd.leasescompanyid = #{companyid}
AND pd.lng >= #{minLng} AND pd.lng <= #{maxLng} AND pd.lat >= #{minLat} AND pd.lat <= #{maxLat}

SELECT pd.*, pv.id vehicleid FROM pub_driver pd, pub_driver_vehicle_ref pdvr, pub_vehicle pv, pub_vehcline pvc, pub_vehcbrand pvb, pub_vehicle_scope pvs, le_vehcline_models_ref lvmr, le_vehiclemodels lv WHERE 1 = 1 AND pvs.`Status` = 1 AND pvc.`Status` = 1 AND pvb.`Status` = 1 AND lv.`Status` = 1 AND pv.`Status` = 1 AND lvmr.`Status` = 1 AND pdvr.`Status` = 1 AND pd.`Status` = 1
AND pd.vehicletype = #{vehicletype} AND pvs.vehicleid = pv.id AND pdvr.driverId = pd.id AND pdvr.vehicleId = pv.id AND lvmr.vehclineid = pv.vehclineid AND lvmr.vehiclemodelsid = lv.Id AND pv.vehclineid = pvc.Id AND pvc.vehcBrandID = pvb.Id AND pd.workstatus IN(${workstatuslist})
AND pd.leasescompanyid = #{companyid}
AND pd.lng >= #{minLng} AND pd.lng <= #{maxLng} AND pd.lat >= #{minLat} AND pd.lat <= #{maxLat}

SELECT pd.*, pv.id vehicleid, ov.level vehiclelevel FROM pub_driver pd,pub_driver_vehicle_ref pdvr,pub_vehicle pv,pub_vehicle_scope pvs, op_vehcline_models_ref ovmr,op_vehiclemodels ov,op_order oo WHERE 1=1 AND pd.`Status` = 1 AND pv.`Status` = 1 AND pdvr.`status` = 1 AND ovmr.`status` = 1 AND ov.`status` = 1 AND oo.`status` = 1 AND pvs.`Status` = 1
AND pd.vehicletype = #{vehicletype} AND pdvr.driverid = pd.id AND pdvr.vehicleid = pv.id AND ovmr.vehclineid = pv.vehclineid AND ovmr.vehiclemodelsid = ov.Id AND pvs.vehicleid = pv.id AND (oo.oncity = pvs.cityid OR oo.offcity = pvs.cityid) AND oo.orderno = #{orderno} AND pd.workstatus IN(${workstatuslist})
AND pd.leasescompanyid = #{companyid}
AND ov.Id = #{cartype}
AND ov.level IN (${levellist})
AND pd.lng >= #{minLng} AND pd.lng <= #{maxLng} AND pd.lat >= #{minLat} AND pd.lat <= #{maxLat}
AND pd.id NOT IN(${alreadySendDrivers})
UNION ALL SELECT pd.*, pv.id vehicleid FROM pub_driver pd,pub_driver_vehicle_ref pdvr,pub_vehicle pv,pub_vehicle_scope pvs, op_vehiclemodels_vehicle_ref ovvr,op_vehiclemodels ov,op_order oo WHERE 1=1 AND pd.`Status` = 1 AND pv.`Status` = 1 AND pdvr.`status` = 1 AND ovvr.`status` = 1 AND ov.`status` = 1 AND oo.`status` = 1 AND pvs.`Status` = 1
AND pd.vehicletype = #{vehicletype} AND pdvr.driverid = pd.id AND pdvr.vehicleid = pv.id AND ovvr.vehicleid = pv.id AND ovvr.vehiclemodelsid = ov.Id AND pvs.vehicleid = pv.id AND (oo.oncity = pvs.cityid OR oo.offcity = pvs.cityid) AND oo.orderno = #{orderno} AND pd.workstatus IN(${workstatuslist})
AND pd.leasescompanyid = #{companyid}
AND ov.Id = #{cartype}
AND ov.level IN (${levellist})
AND pd.lng >= #{minLng} AND pd.lng <= #{maxLng} AND pd.lat >= #{minLat} AND pd.lat <= #{maxLat}
AND pd.id NOT IN(${alreadySendDrivers})

#查询租赁端网约车司机 SELECT pd.*, pv.id vehicleid, lv.`level` vehiclelevel FROM pub_lease_organ_relation plor, pub_vehicle_models_ref pvmr, pub_driver_vehicle_ref pdvr, pub_vehicle pv, pub_vehcline pvc, pub_vehcbrand pvb, pub_vehicle_scope pvs, pub_driver pd, le_vehiclemodels lv, org_order oo WHERE 1=1 AND pd.`Status` = 1 AND pv.`Status` = 1 AND lv.`Status` = 1 AND oo.`status` = 1 AND pvs.`Status` = 1 AND pvb.`Status` = 1 AND pvc.`Status` = 1 AND pdvr.`status` = 1 AND plor.`status` = 1 AND pvmr.`status` = 1 AND plor.companyid = pvmr.companyid AND pvmr.leasecompanyid = plor.leasecompanyid AND pvmr.vehicleid = pdvr.vehicleid AND pdvr.driverid = pd.id AND pvs.vehicleid = pvmr.vehicleid AND pvmr.vehicleid = pv.id AND pv.vehclineid = pvc.Id AND pvc.vehcBrandID = pvb.Id AND (oo.oncity = pvs.cityid OR oo.offcity = pvs.cityid) AND pvmr.vehiclemodelsid = lv.Id AND pd.vehicletype = #{vehicletype} AND pd.workstatus IN(${workstatuslist}) AND oo.orderno = #{orderno} AND plor.companyid = #{companyid}
AND lv.Id = #{cartype}
AND lv.level IN (${levellist})
AND pd.lng >= #{minLng} AND pd.lng <= #{maxLng} AND pd.lat >= #{minLat} AND pd.lat <= #{maxLat}
AND pd.id NOT IN(${alreadySendDrivers})

SELECT lvm.* FROM 
`le_vehiclemodels` lvm 
WHERE 1=1
AND lvm.`Status` = 1 
AND lvm.leasescompanyid = #{companyid} 
AND lvm.`level` >= (
	SELECT `level` FROM 
	`le_vehiclemodels` lvm 
	WHERE 1=1
	AND lvm.`Status` = 1
	AND lvm.Id = #{cartype} 
)
ORDER BY lvm.`level` ASC;
