mapper.select:
select * from pub_ordergpsdata where orderno = #{orderno} and gpssource = #{gpssource} and status = 1
and gpsstatus = #{gpsstatus}
order by gpstime asc

select * from pub_ordergpsdata_history where orderno = #{orderno} and gpssource = #{gpssource} and status = 1
and gpsstatus = #{gpsstatus}
order by gpstime asc
