mapper.select:
		SELECT
			psr.*,
			pca.city as citycaption
		FROM
			pub_sendrules psr,
			pub_cityaddr pca
		WHERE 1=1
		AND psr.STATUS = 1
		AND pca.STATUS = 1
		AND pca.id = psr.city
 		AND psr.rulesstate='0'
		AND psr.leasescompanyid=#{leasescompanyid}
		AND psr.vehicletype=#{vehicletype}
 		AND psr.usetype=#{usetype}
 		AND psr.city=#{city}
 		AND psr.platformtype = #{platformtype}

SELECT oo.orderprop,oo.isusenow,oo.username,oo.userphone,oo.ordersource,oo.paymethod,oo.contact, oo.cartype,oo.cartypelogo,oo.organid,oo.pricecopy, TIMESTAMPDIFF( SECOND, oo.starttime, oo.endtime ) times,oo.undertime,oo.ordertime,oo.departuretime,oo.arrivaltime,oo.starttime,oo.endtime, oo.completetime,oo.canceltime,oo.mileage,oo.orderno,oo.ordertype type,oo.onaddress,oo.offaddress, oo.usetime usetime,oo.tripremark remark,oo.orderstatus `status`,oo.paymentstatus paystatus, oo.reviewstatus,oo.onaddrlng onlng,oo.onaddrlat onlat,oo.offaddrlng offlng,oo.offaddrlat offlat, oo.passengers passengers,oo.passengerphone passengerphone,oo.passengericonmin, oo.passengericonmax,oo.city,oo.estimatedtime,oo.estimatedmileage,oo.estimatedcost, oo.orderamount,oo.fltno,oo.falltime,oo.userrate,oo.usercomment,oo.driverid, oo.vehicleid,oo.companyid,oo.oncity cityid,oo.usetype FROM ( ( SELECT 0 orderprop,lv.logo cartypelogo,lv.name cartype,llc.servicesphone contact, pca.city,ou.headportraitmax passengericonmax,ou.headportraitmin passengericonmin, ou.nickname username,ou.account userphone,oo2.ordersource,oo2.paymethod,oo2.organid, oo2.isusenow,oo2.orderno,oo2.ordertype,oo2.usetime,oo2.pricemodel,oo2.companyid, oo2.oncity,oo2.onaddress,oo2.offcity,oo2.offaddress,oo2.onaddrlng,oo2.onaddrlat, oo2.offaddrlng,oo2.offaddrlat,oo2.pricecopy,oo2.undertime,oo2.ordertime,oo2.departuretime, oo2.arrivaltime,oo2.starttime,oo2.endtime,oo2.completetime,oo2.canceltime,oo2.passengers, oo2.passengerphone,oo2.userid,oo2.tripremark,oo2.orderstatus,oo2.paymentstatus, oo2.reviewstatus,oo2.updatetime,oo2.driverid,oo2.vehicleid,oo2.estimatedtime,oo2.estimatedmileage, oo2.estimatedcost,oo2.mileage,oo2.orderamount,oo2.fltno,oo2.falltime,oo2.userrate,oo2.usercomment, oo2.`status`,oo2.usetype FROM org_order oo2 LEFT JOIN le_vehiclemodels lv ON lv.`Status` = 1 AND lv.Id = oo2.pricemodel LEFT JOIN pub_cityaddr pca ON pca.`status` = 1 AND oo2.oncity = pca.id LEFT JOIN org_user ou ON ou.`Status` = 1 AND ou.id = oo2.userid LEFT JOIN le_leasescompany llc ON llc.`Status` = 1 AND llc.id = oo2.companyid WHERE oo2.`Status` = 1 AND oo2.driverid = #{driverid}
AND ( (oo2.orderstatus = #{statusServiceDone} AND oo2.paymentstatus IN (${paystatuslist})) OR oo2.orderstatus = #{statusServiceCancel} )
AND oo2.orderstatus IN (${statuslist}) AND oo2.paymentstatus IN (${paystatuslist})
AND oo2.usetime >= #{starttime} AND oo2.usetime < #{endtime}
AND oo2.endtime >= #{starttime} AND oo2.endtime < #{endtime}
) UNION ( SELECT 1 orderprop,ov.logo cartypelogo,ov.name cartype,opfi.servcietel contact,pca.city, pu.headportraitmax passengericonmax,pu.headportraitmin passengericonmin, pu.nickname username,pu.account userphone,oo2.ordersource,0 paymethod, NULL organid,oo2.isusenow,oo2.orderno,oo2.ordertype,oo2.usetime,oo2.pricemodel, oo2.companyid,oo2.oncity,oo2.onaddress,oo2.offcity,oo2.offaddress,oo2.onaddrlng, oo2.onaddrlat,oo2.offaddrlng,oo2.offaddrlat,oo2.pricecopy,oo2.undertime,oo2.ordertime, oo2.departuretime,oo2.arrivaltime,oo2.starttime,oo2.endtime,oo2.completetime, oo2.canceltime,oo2.passengers,oo2.passengerphone,oo2.userid,oo2.tripremark, oo2.orderstatus,oo2.paymentstatus,oo2.reviewstatus,oo2.updatetime,oo2.driverid, oo2.vehicleid,oo2.estimatedtime,oo2.estimatedmileage,oo2.estimatedcost, oo2.mileage,oo2.orderamount,oo2.fltno,oo2.falltime,oo2.userrate,oo2.usercomment, oo2.`status`,oo2.usetype FROM op_order oo2 LEFT JOIN op_vehiclemodels ov ON ov.`Status` = 1 AND ov.Id = oo2.pricemodel LEFT JOIN pub_cityaddr pca ON pca.`status` = 1 AND oo2.oncity = pca.id LEFT JOIN pe_user pu ON pu.`Status` = 1 AND pu.id = oo2.userid LEFT JOIN op_platforminfo opfi ON opfi.`status` = 1 WHERE oo2.`Status` = 1 AND oo2.driverid = #{driverid}
AND ( (oo2.orderstatus = #{statusServiceDone} AND oo2.paymentstatus IN (${paystatuslist})) OR oo2.orderstatus = #{statusServiceCancel} )
AND oo2.orderstatus IN (${statuslist}) AND oo2.paymentstatus IN (${paystatuslist})
AND oo2.usetime >= #{starttime} AND oo2.usetime < #{endtime}
AND oo2.endtime >= #{starttime} AND oo2.endtime < #{endtime}
) ) oo
ORDER BY oo.usetime ASC
ORDER BY oo.usetime DESC
LIMIT ${iDisplayStart},${iDisplayLength}

SELECT op_taxisendrules.*, pub_cityaddr.city as citycaption FROM op_taxisendrules, pub_cityaddr WHERE op_taxisendrules.STATUS = 1 AND pub_cityaddr.STATUS = 1 AND pub_cityaddr.id = op_taxisendrules.city
AND op_taxisendrules.rulesstate='0' AND op_taxisendrules.usetype='0'
AND op_taxisendrules.city=#{city}

SELECT op_taxisendrules.*, pub_cityaddr.city as citycaption FROM op_taxisendrules, pub_cityaddr WHERE op_taxisendrules.STATUS = 1 AND pub_cityaddr.STATUS = 1 AND pub_cityaddr.id = op_taxisendrules.city
AND op_taxisendrules.rulesstate='0' AND op_taxisendrules.usetype='1'
AND op_taxisendrules.city=#{city}

SELECT DISTINCT pub_driver.*, pub_driver_vehicle_ref.vehicleid, pub_vehcline.name as vehclinename, pub_vehcbrand.name as vehcbrandname, concat(( SELECT pub_dictionary.text FROM pub_dictionary WHERE pub_dictionary.type = '车牌省' AND pub_dictionary.value = pub_vehicle.platenoprovince AND pub_dictionary.status = 1 limit 0,1 ), ( SELECT pub_dictionary.text FROM pub_dictionary WHERE pub_dictionary.type = '车牌市' AND pub_dictionary.value = pub_vehicle.platenocity AND pub_dictionary.status = 1 limit 0,1 ), pub_vehicle.plateno ) as plateno FROM pub_driver LEFT JOIN pub_driver_vehicle_ref ON pub_driver_vehicle_ref.driverid = pub_driver.id LEFT JOIN pub_vehicle ON pub_driver_vehicle_ref.vehicleid = pub_vehicle.id LEFT JOIN pub_vehcline ON pub_vehicle.vehclineid = pub_vehcline.id LEFT JOIN pub_vehcbrand ON pub_vehcbrand.id = pub_vehcline.vehcBrandID LEFT JOIN pub_vehicle_scope ON pub_vehicle.id = pub_vehicle_scope.vehicleid WHERE pub_driver.status = 1 AND pub_driver_vehicle_ref.status = 1 AND pub_vehicle.status=1 AND pub_vehcline.status=1 AND pub_vehcbrand.status=1 AND pub_vehicle_scope.status=1
AND pub_driver.phone not in (select account from pe_user where pe_user.id = #{userid} and pe_user.status = 1)
AND pub_driver.vehicletype=1 AND pub_driver.workstatus in ('0','1') AND pub_driver.passworkstatus in ('0','1')
AND ( pub_vehicle_scope.cityid=#{city} or pub_vehicle_scope.cityid=#{offcity} )
AND pub_driver.belongleasecompany = #{belongleasecompany}
AND pub_driver.lng >= #{minLng} AND pub_driver.lng <= #{maxLng} AND pub_driver.lat >= #{minLat} AND pub_driver.lat <= #{maxLat}

SELECT DISTINCT pub_driver.*, pub_driver_vehicle_ref.vehicleid, pub_vehcline.name as vehclinename, pub_vehcbrand.name as vehcbrandname, getPlatenoStr(pub_vehicle.platenoprovince,pub_vehicle.platenocity,pub_vehicle.plateno) as plateno ,op_vehiclemodels.`level` vehiclelevel FROM pub_driver LEFT JOIN pub_driver_vehicle_ref ON pub_driver_vehicle_ref.driverid = pub_driver.id AND pub_driver_vehicle_ref.status = 1 LEFT JOIN pub_vehicle ON pub_driver_vehicle_ref.vehicleid = pub_vehicle.id AND pub_vehicle.status=1 LEFT JOIN pub_vehcline ON pub_vehicle.vehclineid = pub_vehcline.id AND pub_vehcline.status=1 LEFT JOIN pub_vehcbrand ON pub_vehcbrand.id = pub_vehcline.vehcBrandID AND pub_vehcbrand.status=1 LEFT JOIN pub_vehicle_scope ON pub_vehicle.id = pub_vehicle_scope.vehicleid AND pub_vehicle_scope.status=1 LEFT JOIN op_vehcline_models_ref ON pub_vehicle.vehclineid = op_vehcline_models_ref.vehclineid and op_vehcline_models_ref.`Status` ='1' LEFT JOIN op_vehiclemodels_vehicle_ref ref on ref.vehicleid = pub_vehicle.id and ref.status =1 LEFT JOIN op_vehiclemodels on op_vehiclemodels.`Status` = '1' and (ref.vehiclemodelsid = op_vehiclemodels.id or op_vehiclemodels.id = op_vehcline_models_ref.vehiclemodelsid) WHERE pub_driver.status = 1 and pub_driver.jobstatus =0
AND pub_driver.phone not in (select account from pe_user where pe_user.id = #{userid} and pe_user.status = 1)
AND pub_driver.vehicletype=#{vehicleType}
and op_vehiclemodels.id is not null
AND (pub_driver.passworkstatus in ('0','1','') or pub_driver.passworkstatus is null)
AND pub_driver.workstatus in
#{status}
AND(pub_vehicle_scope.cityid=#{city} or pub_vehicle_scope.cityid=#{offcity})
and (ref.vehiclemodelsid in
#{modelId}
or op_vehcline_models_ref.vehiclemodelsid in
#{modelId}
)
AND pub_driver.belongleasecompany = #{belongleasecompany}
and ((pub_driver.leasescompanyid in (select le_leasescompany.id from le_leasescompany where le_leasescompany.tocstate = "2" and le_leasescompany.status = 1) and pub_driver.platformtype = "1") or pub_driver.platformtype = "0")
AND pub_driver.lng >= #{minLng} AND pub_driver.lng <= #{maxLng} AND pub_driver.lat >= #{minLat} AND pub_driver.lat <= #{maxLat}
order by vehiclelevel asc

SELECT DISTINCT pub_driver.*, pub_driver_vehicle_ref.vehicleid, pub_vehcline.name as vehclinename, pub_vehcbrand.name as vehcbrandname, getPlatenoStr(pub_vehicle.platenoprovince,pub_vehicle.platenocity,pub_vehicle.plateno) as plateno ,op_vehiclemodels.`level` vehiclelevel FROM pub_driver LEFT JOIN pub_driver_vehicle_ref ON pub_driver_vehicle_ref.driverid = pub_driver.id AND pub_driver_vehicle_ref.status = 1 LEFT JOIN pub_vehicle ON pub_driver_vehicle_ref.vehicleid = pub_vehicle.id AND pub_vehicle.status=1 LEFT JOIN pub_vehcline ON pub_vehicle.vehclineid = pub_vehcline.id AND pub_vehcline.status=1 LEFT JOIN pub_vehcbrand ON pub_vehcbrand.id = pub_vehcline.vehcBrandID AND pub_vehcbrand.status=1 LEFT JOIN pub_vehicle_scope ON pub_vehicle.id = pub_vehicle_scope.vehicleid AND pub_vehicle_scope.status=1 LEFT JOIN op_vehcline_models_ref ON pub_vehicle.vehclineid = op_vehcline_models_ref.vehclineid and op_vehcline_models_ref.`Status` ='1' LEFT JOIN op_vehiclemodels_vehicle_ref ref on ref.vehicleid = pub_vehicle.id and ref.status =1 LEFT JOIN op_vehiclemodels on op_vehiclemodels.`Status` = '1' and (ref.vehiclemodelsid = op_vehiclemodels.id or op_vehiclemodels.id = op_vehcline_models_ref.vehiclemodelsid)
and op_vehiclemodels.`level` >= (SELECT t1.`level`FROM op_vehiclemodels t1 WHERE t1.id = #{selectedModelId})
and op_vehiclemodels.id = #{selectedModelId}
WHERE pub_driver.status = 1 and pub_driver.jobstatus =0
AND pub_driver.phone not in (select account from pe_user where pe_user.id = #{userid} and pe_user.status = 1)
AND pub_driver.vehicletype=#{vehicleType}
and op_vehiclemodels.id is not null
AND (pub_driver.passworkstatus in ('0','1','') or pub_driver.passworkstatus is null)
AND pub_driver.workstatus in
#{status}
AND(pub_vehicle_scope.cityid=#{city} or pub_vehicle_scope.cityid=#{offcity})
AND pub_driver.belongleasecompany = #{belongleasecompany}
and ((pub_driver.leasescompanyid in (select le_leasescompany.id from le_leasescompany where le_leasescompany.tocstate = "2" and le_leasescompany.status = 1) and pub_driver.platformtype = "1") or pub_driver.platformtype = "0")
AND pub_driver.lng >= #{minLng} AND pub_driver.lng <= #{maxLng} AND pub_driver.lat >= #{minLat} AND pub_driver.lat <= #{maxLat}
order by op_vehiclemodels.level asc

SELECT DISTINCT pub_driver.*, pub_driver_vehicle_ref.vehicleid, pub_vehcline.name as vehclinename, pub_vehcbrand.name as vehcbrandname, concat(( SELECT pub_dictionary.text FROM pub_dictionary WHERE pub_dictionary.type = '车牌省' AND pub_dictionary.value = pub_vehicle.platenoprovince AND pub_dictionary.status = 1 limit 0,1 ), ( SELECT pub_dictionary.text FROM pub_dictionary WHERE pub_dictionary.type = '车牌市' AND pub_dictionary.value = pub_vehicle.platenocity AND pub_dictionary.status = 1 limit 0,1 ), pub_vehicle.plateno ) as plateno FROM pub_driver LEFT JOIN pub_driver_vehicle_ref ON pub_driver_vehicle_ref.driverid = pub_driver.id LEFT JOIN pub_vehicle ON pub_driver_vehicle_ref.vehicleid = pub_vehicle.id LEFT JOIN pub_vehcline ON pub_vehicle.vehclineid = pub_vehcline.id LEFT JOIN pub_vehcbrand ON pub_vehcbrand.id = pub_vehcline.vehcBrandID LEFT JOIN pub_vehicle_scope ON pub_vehicle.id = pub_vehicle_scope.vehicleid WHERE pub_driver.status = 1 AND pub_driver_vehicle_ref.status = 1 AND pub_vehicle.status=1 AND pub_vehcline.status=1 AND pub_vehcbrand.status=1 AND pub_vehicle_scope.status=1
AND pub_driver.phone not in (select account from pe_user where pe_user.id = #{userid} and pe_user.status = 1)
AND pub_driver.vehicletype=1 AND pub_driver.workstatus in ('0','1') AND pub_driver.passworkstatus in ('0','1')
AND ( pub_vehicle_scope.cityid=#{city} or pub_vehicle_scope.cityid=#{offcity} )
AND pub_driver.belongleasecompany = #{belongleasecompany}
AND pub_driver.lng >= #{minLng} AND pub_driver.lng <= #{maxLng} AND pub_driver.lat >= #{minLat} AND pub_driver.lat <= #{maxLat}

select * from pub_sendrules t
where t.status = 1

	and t.city = #{cityCode}


	and t.vehicletype = #{vehicleType}


	and t.platformtype = #{platformType}


	and t.usetype = #{useType}


	and t.sendtype = #{sendType}

and t.rulesstate ='0'

SELECT DISTINCT pub_driver.*, pub_driver_vehicle_ref.vehicleid, pub_vehcline.name as vehclinename, pub_vehcbrand.name as vehcbrandname, concat(( SELECT pub_dictionary.text FROM pub_dictionary WHERE pub_dictionary.type = '车牌省' AND pub_dictionary.value = pub_vehicle.platenoprovince AND pub_dictionary.status = 1 limit 0,1 ), ( SELECT pub_dictionary.text FROM pub_dictionary WHERE pub_dictionary.type = '车牌市' AND pub_dictionary.value = pub_vehicle.platenocity AND pub_dictionary.status = 1 limit 0,1 ), pub_vehicle.plateno ) as plateno FROM pub_driver LEFT JOIN pub_driver_vehicle_ref ON pub_driver_vehicle_ref.driverid = pub_driver.id LEFT JOIN pub_vehicle ON pub_driver_vehicle_ref.vehicleid = pub_vehicle.id LEFT JOIN pub_vehcline ON pub_vehicle.vehclineid = pub_vehcline.id LEFT JOIN pub_vehcbrand ON pub_vehcbrand.id = pub_vehcline.vehcBrandID LEFT JOIN pub_vehicle_scope ON pub_vehicle.id = pub_vehicle_scope.vehicleid WHERE pub_driver.status = 1 AND pub_driver_vehicle_ref.status = 1 AND pub_vehicle.status=1 AND pub_vehcline.status=1 AND pub_vehcbrand.status=1 AND pub_vehicle_scope.status=1
AND pub_driver.phone not in (select account from pe_user where pe_user.id = #{userid} and pe_user.status = 1)
AND pub_driver.vehicletype=1 AND pub_driver.workstatus in ('0','1','2') AND pub_driver.passworkstatus in ('0','1')
AND ( pub_vehicle_scope.cityid=#{city} or pub_vehicle_scope.cityid=#{offcity} )
AND pub_driver.belongleasecompany = #{belongleasecompany}
AND pub_driver.lng >= #{minLng} AND pub_driver.lng <= #{maxLng} AND pub_driver.lat >= #{minLat} AND pub_driver.lat <= #{maxLat}
