mapper.select:
SELECT pjl.* FROM pub_jpushlog pjl 
WHERE 1=1
AND pjl.`status` = 1
AND pjl.orderno = #{orderno} 
AND pjl.driverid = #{driverid}

SELECT
	pdn.*
FROM
	`pub_drivernews` pdn
WHERE pdn.`Status` = 1
AND pdn.userid = #{driverid}

AND pdn.newsstate = #{state}

ORDER BY pdn.createtime DESC
LIMIT ${iDisplayStart},${iDisplayLength}

SELECT ls.* FROM pub_sendrules ls 
WHERE ls.`status` = 1
AND ls.leasescompanyid = #{companyid}
AND ls.city = #{city}
and platformtype = "1" and vehicletype = 0

SELECT
	COUNT(oo.orderno) totalcount
FROM
(
	(
		SELECT
			oo2.orderno
		FROM
			org_order oo2
		WHERE oo2.`Status` = 1
		AND oo2.driverid = #{driverid}

		AND oo2.orderstatus IN (${statuslist})



				AND oo2.usetime >= #{starttime}
				AND oo2.usetime < #{endtime}


				AND oo2.endtime >= #{starttime}
				AND oo2.endtime < #{endtime}


	)
	UNION
	(
		SELECT
			oo2.orderno
		FROM
			op_order oo2
		WHERE oo2.`Status` = 1
		AND oo2.driverid = #{driverid}

		AND oo2.orderstatus IN (${statuslist})



				AND oo2.usetime >= #{starttime}
				AND oo2.usetime < #{endtime}


				AND oo2.endtime >= #{starttime}
				AND oo2.endtime < #{endtime}


	)
) oo

SELECT oo.orderprop, oo.isusenow, oo.username, oo.userphone, oo.ordersource, oo.paymethod, oo.contact, oo.cartype, oo.cartypelogo, oo.organid, oo.pricecopy, TIMESTAMPDIFF( SECOND, oo.starttime, oo.endtime ) times, oo.undertime, oo.ordertime, oo.departuretime, oo.arrivaltime, oo.starttime, oo.endtime, oo.completetime, oo.canceltime, oo.mileage, oo.orderno, oo.ordertype type, oo.onaddress, oo.offaddress, oo.usetime usetime, oo.tripremark remark, oo.orderstatus `status`, oo.paymentstatus paystatus, oo.reviewstatus, oo.onaddrlng onlng, oo.onaddrlat onlat, oo.offaddrlng offlng, oo.offaddrlat offlat, oo.passengers passengers, oo.passengerphone passengerphone, oo.passengericonmin, oo.passengericonmax, oo.city, oo.estimatedtime, oo.estimatedmileage, oo.estimatedcost, oo.orderamount, oo.fltno, oo.falltime, oo.userrate, oo.usercomment, oo.driverid, oo.vehicleid, oo.companyid, oo.oncity cityid, oo.usetype FROM ( ( SELECT 0 orderprop, lv.logo cartypelogo, lv.name cartype, llc.servicesphone contact, pca.city, ou.headportraitmax passengericonmax, ou.headportraitmin passengericonmin, ou.nickname username, ou.account userphone, oo2.ordersource, oo2.paymethod, oo2.organid, oo2.isusenow, oo2.orderno, oo2.ordertype, oo2.usetime, oo2.pricemodel, oo2.companyid, oo2.oncity, oo2.onaddress, oo2.offcity, oo2.offaddress, oo2.onaddrlng, oo2.onaddrlat, oo2.offaddrlng, oo2.offaddrlat, oo2.pricecopy, oo2.undertime, oo2.ordertime, oo2.departuretime, oo2.arrivaltime, oo2.starttime, oo2.endtime, oo2.completetime, oo2.canceltime, oo2.passengers, oo2.passengerphone, oo2.userid, oo2.tripremark, oo2.orderstatus, oo2.paymentstatus, oo2.reviewstatus, oo2.updatetime, oo2.driverid, oo2.vehicleid, oo2.estimatedtime, oo2.estimatedmileage, oo2.estimatedcost, oo2.mileage, oo2.orderamount, oo2.fltno, oo2.falltime, oo2.userrate, oo2.usercomment, oo2.`status`, oo2.usetype FROM org_order oo2 LEFT JOIN le_vehiclemodels lv ON lv.`Status` = 1 AND lv.Id = oo2.pricemodel LEFT JOIN pub_cityaddr pca ON pca.`status` = 1 AND oo2.oncity = pca.id LEFT JOIN org_user ou ON ou.`Status` = 1 AND ou.id = oo2.userid LEFT JOIN le_leasescompany llc ON llc.`Status` = 1 AND llc.id = oo2.companyid WHERE oo2.`Status` = 1 AND oo2.driverid = #{driverid} AND oo2.orderstatus IN (${statuslist})
AND oo2.paymentstatus IN (${paystatuslist})
AND oo2.usetime >= #{starttime} AND oo2.usetime < #{endtime}
AND oo2.endtime >= #{starttime} AND oo2.endtime < #{endtime}
) UNION ( SELECT 1 orderprop, ov.logo cartypelogo, ov.name cartype, opfi.servcietel contact, pca.city, pu.headportraitmax passengericonmax, pu.headportraitmin passengericonmin, pu.nickname username, pu.account userphone, oo2.ordersource, 0 paymethod, NULL organid, oo2.isusenow, oo2.orderno, oo2.ordertype, oo2.usetime, oo2.pricemodel, oo2.companyid, oo2.oncity, oo2.onaddress, oo2.offcity, oo2.offaddress, oo2.onaddrlng, oo2.onaddrlat, oo2.offaddrlng, oo2.offaddrlat, oo2.pricecopy, oo2.undertime, oo2.ordertime, oo2.departuretime, oo2.arrivaltime, oo2.starttime, oo2.endtime, oo2.completetime, oo2.canceltime, oo2.passengers, oo2.passengerphone, oo2.userid, oo2.tripremark, oo2.orderstatus, oo2.paymentstatus, oo2.reviewstatus, oo2.updatetime, oo2.driverid, oo2.vehicleid, oo2.estimatedtime, oo2.estimatedmileage, oo2.estimatedcost, oo2.mileage, oo2.orderamount, oo2.fltno, oo2.falltime, oo2.userrate, oo2.usercomment, oo2.`status`, oo2.usetype FROM op_order oo2 LEFT JOIN op_vehiclemodels ov ON ov.`Status` = 1 AND ov.Id = oo2.pricemodel LEFT JOIN pub_cityaddr pca ON pca.`status` = 1 AND oo2.oncity = pca.id LEFT JOIN pe_user pu ON pu.`Status` = 1 AND pu.id = oo2.userid LEFT JOIN op_platforminfo opfi ON opfi.`status` = 1 WHERE oo2.`Status` = 1 AND oo2.driverid = #{driverid} AND oo2.orderstatus IN (${statuslist})
AND oo2.paymentstatus IN (${paystatuslist})
AND oo2.usetime >= #{starttime} AND oo2.usetime < #{endtime}
AND oo2.endtime >= #{starttime} AND oo2.endtime < #{endtime}
) ) oo
ORDER BY oo.usetime ASC
ORDER BY oo.usetime DESC
LIMIT ${iDisplayStart},${iDisplayLength}

SELECT SUM(orderstatis.count) count, SUM(orderstatis.cost) cost, orderstatis.`YEAR`, orderstatis.`MONTH` FROM ( ( SELECT COUNT(oo.orderno) count, SUM(IFNULL(oo.shouldpayamount,oo.orderamount)) cost, DATE_FORMAT(IFNULL(oo.endtime,oo.canceltime), "%Y") YEAR, DATE_FORMAT(IFNULL(oo.endtime,oo.canceltime), "%m") MONTH FROM `op_order` oo WHERE oo.`status` = 1 AND oo.orderstatus in (${orderstatuslist}) AND oo.paymentstatus in (${paystatuslist}) AND oo.driverid = #{driverid} AND (oo.endtime IS NOT NULL OR oo.canceltime IS NOT NULL)
GROUP BY YEAR,MONTH
GROUP BY YEAR
) UNION ( SELECT COUNT(oo.orderno) count, SUM(IFNULL(oo.shouldpayamount,oo.orderamount)) cost, DATE_FORMAT(IFNULL(oo.endtime,oo.canceltime), "%Y") YEAR, DATE_FORMAT(IFNULL(oo.endtime,oo.canceltime), "%m") MONTH FROM `org_order` oo WHERE oo.`status` = 1 AND oo.orderstatus in (${orderstatuslist}) AND oo.paymentstatus in (${paystatuslist}) AND oo.driverid = #{driverid} AND oo.paymethod != #{paymethod} AND (oo.endtime IS NOT NULL OR oo.canceltime IS NOT NULL)
GROUP BY YEAR,MONTH
GROUP BY YEAR
) ) orderstatis
GROUP BY YEAR,MONTH ORDER BY YEAR DESC,MONTH DESC
GROUP BY YEAR ORDER BY YEAR DESC
LIMIT ${iDisplayStart},${iDisplayLength}

mapper.update:
UPDATE
org_order oo
op_order oo
SET
oo.companyid=#{companyid},
oo.updatetime=NOW(), oo.undertime=#{undertime}, oo.ordertime=#{ordertime}, oo.departuretime=#{departuretime}, oo.arrivaltime=#{arrivaltime}, oo.starttime=#{starttime}, oo.endtime=#{endtime}, oo.completetime=#{completetime}, oo.canceltime=#{canceltime}, oo.cancelparty=#{cancelparty}, oo.orderstatus=#{status}, oo.paymentstatus=#{paystatus}, oo.reviewstatus=#{reviewstatus}, oo.driverid = #{driverid}
,oo.departurecity = #{cityintime}, oo.departureaddress = #{addressintime}, oo.departurelng = #{lng}, oo.departurelat = #{lat}
,oo.arrivalcity = #{cityintime}, oo.arrivaladdress = #{addressintime}, oo.arrivallng = #{lng}, oo.arrivallat = #{lat}
,oo.startcity = #{cityintime}, oo.startaddress = #{addressintime}, oo.startlng = #{lng}, oo.startllat = #{lat}
,oo.endcity = #{cityintime}, oo.endaddress = #{addressintime}, oo.endlng = #{lng}, oo.endllat = #{lat}
WHERE oo.`status` = 1 AND oo.orderno = #{orderno}
