mapper.select:
select pc.id,pc.city,count(*) as totalcount from pub_coupon_activity_use_city pcauc
INNER join pub_cityaddr pc on pcauc.city=pc.id 
INNER join pub_coupon coupon on coupon.couponactivyidref=pcauc.couponactivityidref
where pcauc.couponactivityidref in 
  (select id from pub_coupon_activity where platformtype=1 and sendruletarget=2 and lecompanyid=#{lecompanyid} and status=1)

    and date_format(coupon.createtime,'%Y-%m-%d')    >=   #{usedstarttime}


    and date_format(coupon.createtime,'%Y-%m-%d')    <=   #{usedendtime}


    and pc.id=#{city}

  group by pcauc.city

select count(*) from (
  select pc.id from pub_coupon_activity_use_city pcauc
    INNER join pub_cityaddr pc on pcauc.city=pc.id 
    INNER join pub_coupon coupon on coupon.couponactivyidref=pcauc.couponactivityidref
  where pcauc.couponactivityidref in 
    (select id from pub_coupon_activity where platformtype=1 and sendruletarget=2 and lecompanyid=#{lecompanyid} and status=1)

    and date_format(coupon.createtime,'%Y-%m-%d')    >=   #{usedstarttime}


    and date_format(coupon.createtime,'%Y-%m-%d')    <=   #{usedendtime}


    and pc.id=#{city}

    group by pcauc.city) v

select ifnull(count(*),0) from org_user 
  where organid in 
    (select organid from org_organ_company_ref where companyid=#{lecompanyid} and status='1')
    and status='1'

    and date_format(registertime,'%Y-%m-%d')    >=   #{starttime}


    and date_format(registertime,'%Y-%m-%d')    <=   #{endtime}

select ifnull(COUNT(DISTINCT ex.userid),0) from org_userexpenses ex where exists ( select ou.id from org_user ou where ou.organid in (select organid from org_organ_company_ref where companyid=#{lecompanyid} and status='1') and status='1'
and date_format(registertime,'%Y-%m-%d') >= #{starttime}
and date_format(registertime,'%Y-%m-%d') <= #{endtime}
and ex.userid=ou.id ) and ex.tradetype='0' and ex.operateresult='0'
