SELECT pd.*, pv.id vehicleid FROM pub_driver pd, pub_driver_vehicle_ref pdvr, pub_vehicle pv, pub_vehcline pvc, pub_vehcbrand pvb, pub_vehicle_scope pvs, op_vehiclemodels_vehicle_ref ovvr, op_vehiclemodels ov WHERE 1 = 1 AND pvs.`Status` = 1 AND pvc.`Status` = 1 AND pvb.`Status` = 1 AND ov.`Status` = 1 AND pv.`Status` = 1 AND ovvr.`Status` = 1 AND pdvr.`Status` = 1 AND pd.`Status` = 1
AND pd.vehicletype = 1 AND pvs.vehicleid = pv.id AND pdvr.driverId = pd.id AND pdvr.vehicleId = pv.id AND ovvr.vehicleid = pv.id AND ovvr.vehiclemodelsid = ov.Id AND pv.vehclineid = pvc.Id AND pvc.vehcBrandID = pvb.Id AND pd.workstatus = 0
AND pd.leasescompanyid = #{companyid}
AND pvs.cityid = #{city}
AND pd.lng >= #{minLng} AND pd.lng <= #{maxLng} AND pd.lat >= #{minLat} AND pd.lat <= #{maxLat}

SELECT pd.*, pv.id vehicleid FROM pub_driver pd, pub_driver_vehicle_ref pdvr, pub_vehicle pv, pub_vehcline pvc, pub_vehcbrand pvb, pub_vehicle_scope pvs, op_vehiclemodels_vehicle_ref ovvr, op_vehiclemodels ov WHERE 1 = 1 AND pvs.`Status` = 1 AND pvc.`Status` = 1 AND pvb.`Status` = 1 AND ov.`Status` = 1 AND pv.`Status` = 1 AND ovvr.`Status` = 1 AND pdvr.`Status` = 1 AND pd.`Status` = 1
AND pd.vehicletype = 0 AND pvs.vehicleid = pv.id AND pdvr.driverId = pd.id AND pdvr.vehicleId = pv.id AND ovvr.vehicleid = pv.id AND ovvr.vehiclemodelsid = ov.Id AND pv.vehclineid = pvc.Id AND pvc.vehcBrandID = pvb.Id AND pd.workstatus = 0
AND pd.leasescompanyid = #{companyid}
AND pvs.cityid = #{city}
AND pd.lng >= #{minLng} AND pd.lng <= #{maxLng} AND pd.lat >= #{minLat} AND pd.lat <= #{maxLat}

SELECT pd.* ,pv.id vehicleid FROM pub_driver pd, pub_driver_vehicle_ref pdvr, pub_vehicle pv, pub_vehcline pvc, pub_vehcbrand pvb, pub_vehicle_scope pvs, le_vehcline_models_ref lvmr, le_vehiclemodels lv WHERE 1 = 1 AND pvs.`Status` = 1 AND pvc.`Status` = 1 AND pvb.`Status` = 1 AND lv.`Status` = 1 AND pv.`Status` = 1 AND lvmr.`Status` = 1 AND pdvr.`Status` = 1 AND pd.`Status` = 1 AND pd.jobstatus = 0
AND pd.vehicletype = 0 AND pvs.vehicleid = pv.id AND pdvr.driverId = pd.id AND pdvr.vehicleId = pv.id AND lvmr.vehclineid = pv.vehclineid AND lvmr.vehiclemodelsid = lv.Id AND pv.vehclineid = pvc.Id AND pvc.vehcBrandID = pvb.Id AND pd.workstatus = 0 AND pd.leasescompanyid = #{companyid} AND pvs.cityid = #{city}
AND pd.lng >= #{minLng} AND pd.lng <= #{maxLng} AND pd.lat >= #{minLat} AND pd.lat <= #{maxLat}

SELECT oo.orderno, pd.id, pd.`name`, pd.phone, pd.lng, pd.lat FROM pub_driver pd,pub_driver_vehicle_ref pdvr,pub_vehicle pv,pub_vehicle_scope pvs, op_vehcline_models_ref ovmr,op_vehiclemodels ov,op_order oo WHERE 1=1 AND pd.`Status` = 1 AND pv.`Status` = 1 AND pdvr.`status` = 1 AND ovmr.`status` = 1 AND ov.`status` = 1 AND oo.`status` = 1 AND pvs.`Status` = 1
AND pd.vehicletype = #{vehicletype} AND pdvr.driverid = pd.id AND pdvr.vehicleid = pv.id AND ovmr.vehclineid = pv.vehclineid AND ovmr.vehiclemodelsid = ov.Id AND pvs.vehicleid = pv.id AND (oo.oncity = pvs.cityid OR oo.offcity = pvs.cityid) AND oo.orderno = #{orderno}
AND pd.leasescompanyid = #{companyid}
AND ov.Id = #{cartype}
AND ov.level >= (SELECT tov.level FROM op_vehiclemodels tov WHERE tov.`Status` = 1 AND tov.id = #{cartype})
AND pd.workstatus IN(${workstatuslist})
AND pd.lng >= #{minLng} AND pd.lng <= #{maxLng} AND pd.lat >= #{minLat} AND pd.lat <= #{maxLat}
AND pd.id NOT IN(${alreadySendDrivers})
UNION ALL SELECT oo.orderno, pd.id, pd.`name`, pd.phone, pd.lng, pd.lat FROM pub_driver pd,pub_driver_vehicle_ref pdvr,pub_vehicle pv,pub_vehicle_scope pvs, op_vehiclemodels_vehicle_ref ovvr,op_vehiclemodels ov,op_order oo WHERE 1=1 AND pd.`Status` = 1 AND pv.`Status` = 1 AND pdvr.`status` = 1 AND ovvr.`status` = 1 AND ov.`status` = 1 AND oo.`status` = 1 AND pvs.`Status` = 1
AND pd.vehicletype = #{vehicletype} AND pdvr.driverid = pd.id AND pdvr.vehicleid = pv.id AND ovvr.vehicleid = pv.id AND ovvr.vehiclemodelsid = ov.Id AND pvs.vehicleid = pv.id AND (oo.oncity = pvs.cityid OR oo.offcity = pvs.cityid) AND oo.orderno = #{orderno}
AND pd.leasescompanyid = #{companyid}
AND ov.Id = #{cartype}
AND ov.level >= (SELECT tov.level FROM op_vehiclemodels tov WHERE tov.`Status` = 1 AND tov.id = #{cartype})
AND pd.workstatus IN(${workstatuslist})
AND pd.lng >= #{minLng} AND pd.lng <= #{maxLng} AND pd.lat >= #{minLat} AND pd.lat <= #{maxLat}
AND pd.id NOT IN(${alreadySendDrivers})

SELECT oo.orderno, pd.id, pd.`name`, pd.phone, pd.lng, pd.lat FROM pub_driver pd, pub_driver_vehicle_ref pdvr, pub_vehicle pv, pub_vehcline pvc, pub_vehcbrand pvb, pub_vehicle_scope pvs, le_vehcline_models_ref lvmr, le_vehiclemodels lv, org_order oo WHERE 1 = 1 AND oo.`status` = 1 AND pvs.`Status` = 1 AND pvc.`Status` = 1 AND pvb.`Status` = 1 AND lv.`Status` = 1 AND pv.`Status` = 1 AND lvmr.`Status` = 1 AND pdvr.`Status` = 1 AND pd.`Status` = 1
AND pd.vehicletype = 0 AND pvs.vehicleid = pv.id AND pdvr.driverId = pd.id AND pdvr.vehicleId = pv.id AND lvmr.vehclineid = pv.vehclineid AND lvmr.vehiclemodelsid = lv.Id AND pv.vehclineid = pvc.Id AND pvc.vehcBrandID = pvb.Id AND (oo.oncity = pvs.cityid OR oo.offcity = pvs.cityid) AND oo.orderno = #{orderno} AND pd.leasescompanyid = #{companyid}
AND lv.Id = #{cartype}
AND lv.level >= (SELECT tlv.level FROM le_vehiclemodels tlv WHERE tlv.`Status` = 1 AND tlv.id = #{cartype})
AND pd.workstatus IN(${workstatuslist})
AND pd.lng >= #{minLng} AND pd.lng <= #{maxLng} AND pd.lat >= #{minLat} AND pd.lat <= #{maxLat}
AND pd.id NOT IN(${alreadySendDrivers})

SELECT oo.*, (SELECT city FROM pub_cityaddr WHERE pub_cityaddr.status=1 AND pub_cityaddr.id=oo.city) AS citycaption FROM `org_organ` oo WHERE id IN ( SELECT ou.organid FROM org_user ou
WHERE ou.`status` = 1
OR ( ou.`Status` = 2 AND ( SELECT oua.balance FROM org_useraccount oua WHERE oua.status = 1 AND oua.userid = ou.id AND oua.leasescompanyid = #{companyid} ) > 0 )
AND ou.id = #{userid}
)

SELECT fav.*,CONCAT(fav.name,fav.phone) namephone FROM ( ( SELECT ou.id, ou.id userid, ou.nickname `name`, ou.account phone, ou.createtime, ou.updatetime, ou.id creater, ou.id updater, ou.`Status` FROM org_user ou WHERE ou.`status` = 1 AND ou.id = #{userid} ) UNION ( SELECT om.* FROM pub_mostcontact om, org_user ou WHERE om.`status` = 1 AND ou.`status` = 1 AND om.userphone = ou.account AND om.phone != ou.account AND ou.id = #{userid} ) ) fav
WHERE (fav.phone like '%${sSearch}%' OR fav.name like '%${sSearch}%')
GROUP BY CONCAT(fav.name,fav.phone) LIMIT ${iDisplayStart},${iDisplayLength}

select org_user.id, case when (select count(*) from org_order where org_order.status = 1 and org_order.userid = org_user.id and org_order.orderstatus = #{orderstatus} and (org_order.paymentstatus = #{paymentstatus} or isnull(org_order.paymentstatus))) > 0 then concat(org_user.account, " ", ifnull(org_user.nickname, ""), "(未支付)") else concat(org_user.account, " ", ifnull(org_user.nickname, "")) end text from org_user left join org_organ on org_user.organid = org_organ.id left join ( select org_organ.* from le_company_rules_ref left join org_organ on le_company_rules_ref.organid = org_organ.id where le_company_rules_ref.leasescompanyid = #{companyid} and le_company_rules_ref.rulestate = "1" and le_company_rules_ref.status = 1
and le_company_rules_ref.organid = #{organid}
) tmpOrgan on org_organ.creditcode = tmpOrgan.creditcode left join pub_cityaddr on tmpOrgan.city = pub_cityaddr.id where org_user.status = 1 and tmpOrgan.status = 1 and org_user.usertype = "0" and org_organ.status = 1 and org_user.id in ( select org_user_rules_ref.userid from org_usecarrules left join org_user_rules_ref on org_usecarrules.id = org_user_rules_ref.userulesid where org_usecarrules.status = 1 and org_user_rules_ref.status = 1 and org_usecarrules.leasescompanyid = #{companyid} and org_usecarrules.usetype = #{ordertype} ) and org_organ.creditcode in ( select org_organ.creditcode from org_organ_company_ref left join org_organ on org_organ_company_ref.organid = org_organ.id where org_organ_company_ref.status = 1 and org_organ_company_ref.bindstate = "1" and org_organ_company_ref.companyid = #{companyid} )
and ( org_user.account like concat("%", #{sSearch}, "%") or org_user.nickname like concat("%", #{sSearch}, "%") )
group by org_user.id limit 0,10

select org_user.id, case when (select count(*) from org_order where org_order.status = 1 and org_order.userid = org_user.id and org_order.orderstatus = #{orderstatus} and (org_order.paymentstatus = #{paymentstatus} or isnull(org_order.paymentstatus))) > 0 then concat(org_user.account, " ", ifnull(org_user.nickname, ""), "(未支付)") else concat(org_user.account, " ", ifnull(org_user.nickname, "")) end text from org_user left join org_organ on org_user.organid = org_organ.id left join (select org_organ.*, org_organ_company_ref.bindstate from org_organ_company_ref left join org_organ on org_organ_company_ref.organid = org_organ.id where org_organ_company_ref.companyid = #{companyid} and org_organ_company_ref.status = 1
and org_organ_company_ref.organid = #{organid}
) tmpOrgan on org_organ.creditcode = tmpOrgan.creditcode left join pub_cityaddr on tmpOrgan.city = pub_cityaddr.id where org_organ.status = 1 and tmpOrgan.status = 1 and org_user.usertype = "0" and ((org_user.status = 1 and tmpOrgan.bindstate = '1') or ((org_user.status != 1 or tmpOrgan.bindstate = '0') and (select org_useraccount.balance from org_useraccount where org_useraccount.userid = org_user.id and org_useraccount.status = 1 and org_useraccount.leasescompanyid = #{companyid}) > 0 ))
and ( org_user.account like concat("%", #{sSearch}, "%") or org_user.nickname like concat("%", #{sSearch}, "%") )
group by org_user.id limit 0,10

select count(*) from (select org_user.id from org_user left join org_organ on org_user.organid = org_organ.id left join ( select org_organ.* from le_company_rules_ref left join org_organ on le_company_rules_ref.organid = org_organ.id where le_company_rules_ref.leasescompanyid = #{companyid} and le_company_rules_ref.rulestate = "1" and le_company_rules_ref.status = 1
and le_company_rules_ref.organid = #{organid}
) tmpOrgan on org_organ.creditcode = tmpOrgan.creditcode left join pub_cityaddr on tmpOrgan.city = pub_cityaddr.id where org_user.status = 1 and tmpOrgan.status = 1 and org_user.usertype = "0" and org_organ.status = 1 and org_user.id in ( select org_user_rules_ref.userid from org_usecarrules left join org_user_rules_ref on org_usecarrules.id = org_user_rules_ref.userulesid where org_usecarrules.status = 1 and org_user_rules_ref.status = 1 and org_usecarrules.leasescompanyid = #{companyid} and org_usecarrules.usetype = #{ordertype} ) and org_organ.creditcode in ( select org_organ.creditcode from org_organ_company_ref left join org_organ on org_organ_company_ref.organid = org_organ.id where org_organ_company_ref.status = 1 and org_organ_company_ref.bindstate = "1" and org_organ_company_ref.companyid = #{companyid} )
and (org_user.account like concat("%", #{sSearch}, "%") or org_user.nickname like concat("%", #{sSearch}, "%"))
group by org_user.id) tmpUser

select count(*) from ( select org_user.id from org_user left join org_organ on org_user.organid = org_organ.id left join (select org_organ.*, org_organ_company_ref.bindstate from org_organ_company_ref left join org_organ on org_organ_company_ref.organid = org_organ.id where org_organ_company_ref.companyid = #{companyid} and org_organ_company_ref.status = 1
and org_organ_company_ref.organid = #{organid}
) tmpOrgan on org_organ.creditcode = tmpOrgan.creditcode left join pub_cityaddr on tmpOrgan.city = pub_cityaddr.id where org_organ.status = 1 and tmpOrgan.status = 1 and org_user.usertype = "0" and ((org_user.status = 1 and tmpOrgan.bindstate = '1') or ((org_user.status != 1 or tmpOrgan.bindstate = '0') and (select org_useraccount.balance from org_useraccount where org_useraccount.userid = org_user.id and org_useraccount.status = 1 and org_useraccount.leasescompanyid = #{companyid}) > 0 ))
and ( org_user.account like concat("%", #{sSearch}, "%") or org_user.nickname like concat("%", #{sSearch}, "%") )
group by org_user.id) tmpUser

select * from (select tmpOrgan.fullname orgcaption, pub_cityaddr.city citycaption, (select count(*) from org_order where org_order.status = 1 and org_order.userid = org_user.id and org_order.orderstatus = #{orderstatus} and (org_order.paymentstatus = #{paymentstatus} or isnull(org_order.paymentstatus))) > 0 notpay, (select count(*) from org_order where org_order.status = 1 and org_order.userid = org_user.id and org_order.orderstatus in (#{orderstatuslist})) notdone, org_user.* from org_user left join org_organ on org_user.organid = org_organ.id left join ( select org_organ.* from le_company_rules_ref left join org_organ on le_company_rules_ref.organid = org_organ.id where le_company_rules_ref.leasescompanyid = #{companyid} and le_company_rules_ref.rulestate = "1" and le_company_rules_ref.status = 1
and le_company_rules_ref.organid = #{organid}
) tmpOrgan on org_organ.creditcode = tmpOrgan.creditcode left join pub_cityaddr on tmpOrgan.city = pub_cityaddr.id where org_user.status = 1 and tmpOrgan.status = 1 and org_user.usertype = "0" and org_organ.status = 1 and org_user.id in ( select org_user_rules_ref.userid from org_usecarrules left join org_user_rules_ref on org_usecarrules.id = org_user_rules_ref.userulesid where org_usecarrules.status = 1 and org_user_rules_ref.status = 1 and org_usecarrules.leasescompanyid = #{companyid} and org_usecarrules.usetype = #{ordertype} ) and org_organ.creditcode in ( select org_organ.creditcode from org_organ_company_ref left join org_organ on org_organ_company_ref.organid = org_organ.id where org_organ_company_ref.status = 1 and org_organ_company_ref.bindstate = "1" and org_organ_company_ref.companyid = #{companyid} )
and ( org_user.account like concat("%", #{sSearch}, "%") or org_user.nickname like concat("%", #{sSearch}, "%") )
group by org_user.id) tmpUser limit ${iDisplayStart},${iDisplayLength}

select * from (select tmpOrgan.fullname orgcaption, pub_cityaddr.city citycaption, (select count(*) from org_order where org_order.status = 1 and org_order.userid = org_user.id and org_order.orderstatus = #{orderstatus} and (org_order.paymentstatus = #{paymentstatus} or isnull(org_order.paymentstatus))) > 0 notpay, (select count(*) from org_order where org_order.status = 1 and org_order.userid = org_user.id and org_order.orderstatus in (#{orderstatuslist})) notdone, org_user.* from org_user left join org_organ on org_user.organid = org_organ.id left join (select org_organ.*, org_organ_company_ref.bindstate from org_organ_company_ref left join org_organ on org_organ_company_ref.organid = org_organ.id where org_organ_company_ref.companyid = #{companyid} and org_organ_company_ref.status = 1
and org_organ_company_ref.organid = #{organid}
) tmpOrgan on org_organ.creditcode = tmpOrgan.creditcode left join pub_cityaddr on tmpOrgan.city = pub_cityaddr.id where org_organ.status = 1 and tmpOrgan.status = 1 and org_user.usertype = "0" and ((org_user.status = 1 and tmpOrgan.bindstate = '1') or ((org_user.status != 1 or tmpOrgan.bindstate = '0') and (select org_useraccount.balance from org_useraccount where org_useraccount.userid = org_user.id and org_useraccount.status = 1 and org_useraccount.leasescompanyid = #{companyid}) > 0 ))
and ( org_user.account like concat("%", #{sSearch}, "%") or org_user.nickname like concat("%", #{sSearch}, "%") )
group by org_user.id) tmpUser limit ${iDisplayStart},${iDisplayLength}

select * from pub_cityaddr
where
	id in (
		select le_accountrules.city from le_accountrules 
		left join le_company_rules_ref on le_accountrules.rulesrefid = le_company_rules_ref.id
		left join org_organ on le_company_rules_ref.organid = org_organ.id
		left join org_organ_company_ref on org_organ.id = org_organ_company_ref.organid
		where le_company_rules_ref.leasescompanyid = #{companyid} and org_organ_company_ref.companyid = #{companyid}
		and org_organ.creditcode = (select org_organ.creditcode from org_user left join org_organ on org_user.organid = org_organ.id where org_user.id = #{userid})
		and le_company_rules_ref.rulestate = '1' and le_accountrules.leasescompanyid = #{companyid}
		and le_accountrules.rulestype = #{ordertype} and le_accountrules.type = '1'
		and le_accountrules.cartype in (
			select org_usecarrules.vehiclemodels from org_usecarrules left join org_user_rules_ref on org_usecarrules.id = org_user_rules_ref.userulesid and org_user_rules_ref.userid = #{userid}
			and org_usecarrules.usetype = #{ordertype} and org_usecarrules.leasescompanyid = #{companyid} and org_usecarrules.status = 1 and org_user_rules_ref.status = 1
		)
		and le_accountrules.status = 1 and le_accountrules.status = 1 and le_company_rules_ref.status = 1 and org_organ.status = 1 and org_organ_company_ref.status = 1
	)
	and id in (
		select city from pub_sendrules where leasescompanyid = #{companyid} and status = 1 and platformtype="1" and vehicletype=0
	)
	and status = 1

		and id = #{city}

order by fullnameinitials

select * from pub_cityaddr where id in ( select city from le_accountrules where leasescompanyid = #{companyid} and rulestype = #{ordertype} and type = "0" and rulesstate = "0" and status = 1 ) and id in ( select city from pub_sendrules where leasescompanyid = #{companyid} and status = 1 and platformtype="1" and vehicletype=0 ) and status = 1
and id = #{city}
order by fullnameinitials
