mapper.select:
		select count(*)
		from org_organbill inner join
	      (select t1.billsid,t1.operationtime,t1.billstate from (
	      select org_organbillstate.billsid,org_organbillstate.operationtime,org_organbillstate.billstate
	      from org_organbillstate
	      where org_organbillstate.status = 1
          order by org_organbillstate.operationtime desc  
	      ) t1 group by t1.billsid 
	      ) t2 on org_organbill.id = t2.billsid
		where org_organbill.leasescompanyid = #{companyId}
	  and org_organbill.status = 1
	  and org_organbill.organid = #{id}
	  and t2.billstate in ('1','2','3','4','5','6','9')

SELECT t.* FROM ( SELECT (@rownum := @rownum + 1) AS indexnum, m.* FROM ( SELECT org_organ.*, org_user.account, ( SELECT pub_cityaddr.city FROM pub_cityaddr WHERE pub_cityaddr.id = org_organ.city AND pub_cityaddr. STATUS = 1 ) AS citycaption FROM org_organ, org_organ_company_ref, org_user, (SELECT @rownum := 0) r WHERE org_organ. STATUS = 1 AND org_organ_company_ref. STATUS = 1 AND org_organ_company_ref.organid = org_organ.id AND org_organ_company_ref.userid = org_user.id AND org_organ_company_ref.companyid = #{leasesCompanyId} AND org_organ.`Status` = 1 AND org_organ_company_ref.`Status` = 1 AND org_user.`Status` = 1
and org_organ.id like "%"#{queryShortName}"%"
and org_organ.id IN ( SELECT le_roledataauthority.dynamicid as organid FROM le_roledataauthority, le_roleuser, le_user WHERE le_roledataauthority.status=1 and le_roleuser.status=1 and le_user.status=1 and le_user.id = le_roleuser.userid and le_roleuser.roleid = le_roledataauthority.roleid and le_user.account=#{account} and le_user.leasescompanyid = #{leasesCompanyId} )
and org_organ.city like "%"#{queryCity}"%"
and org_organ.cooperationstatus = #{cooperationStatus}
and org_organ.id = #{queryCustomertype}
ORDER BY CONVERT(citycaption USING gbk ) COLLATE gbk_chinese_ci , org_organ.CreateTime
) m ) t WHERE
t.indexnum > #{iDisplayStart} and t.indexnum <= (#{iDisplayStart} + #{iDisplayLength})

choose.otherwise:
SELECT org_organ.city AS id, ( SELECT pub_cityaddr.city FROM pub_cityaddr WHERE pub_cityaddr.id = org_organ.city AND pub_cityaddr. STATUS = 1 ) AS text FROM org_organ, org_organ_company_ref WHERE org_organ. STATUS = 1 AND org_organ_company_ref. STATUS = 1 AND org_organ_company_ref.organid = org_organ.id AND org_organ_company_ref.companyid = #{leasesCompanyId}
and ( SELECT pub_cityaddr.city FROM pub_cityaddr WHERE pub_cityaddr.id = org_organ.city AND pub_cityaddr. STATUS = 1 ) like "%"#{queryCity}"%"
GROUP BY city

mapper.select:
SELECT t.* FROM ( SELECT (@rownum := @rownum + 1) AS indexnum, m.* FROM ( SELECT org_organ.*, org_user.account, ( SELECT pub_cityaddr.city FROM pub_cityaddr WHERE pub_cityaddr.id = org_organ.city AND pub_cityaddr. STATUS = 1 ) AS citycaption, org_organ_company_ref.lineofcredit FROM org_organ, org_organ_company_ref, org_user, (SELECT @rownum := 0) r WHERE org_organ. STATUS = 1 AND org_organ_company_ref. STATUS = 1 AND org_organ_company_ref.organid = org_organ.id AND org_organ_company_ref.userid = org_user.id AND org_organ_company_ref.companyid = #{leasesCompanyId} AND org_organ.`Status` = 1 AND org_organ_company_ref.`Status` = 1 AND org_user.`Status` = 1
and org_organ.id like "%"#{queryShortName}"%"
and org_organ.city like "%"#{queryCity}"%"
and org_organ.cooperationstatus = #{cooperationStatus}
and org_organ.id = #{queryCustomertype}
ORDER BY CONVERT(citycaption USING gbk ) COLLATE gbk_chinese_ci , org_organ.CreateTime ) m ) t

select t1.id id, t1.fullname text from org_organ_company_ref t, org_organ t1 where t.organid = t1.id and t1.`Status` = 1 and t.`Status` = 1 and t.companyid = #{leasescompanyid}
and t1.fullname like "%"#{organName}"%"
limit 0, 10

select count(*)
from org_organcreditrecord
where org_organcreditrecord.organid = #{organId}
  and org_organcreditrecord.leasescompanyid = #{leasesCompanyId}
  and org_organcreditrecord.status = 1

SELECT t.*,t2.shortname as companyName FROM pub_coooperate t LEFT JOIN pub_lease_organ_relation t1 ON (t1.`status` = 1 AND t1.companyid = t.companyid AND t1.leasecompanyid = t.leasecompanyid AND t1.organid = #{key}) LEFT JOIN le_leasescompany t2 ON (t.leasecompanyid = t2.id AND t2.`Status` = 1) WHERE 1 = 1 AND t.servicetype = 0 AND t.platformtype = 1 AND t.`status` = 1 AND t.companyid = #{leasescompanyid}
and t.id = #{queryForTheCarBody}
AND t1.Id IS NULL LIMIT #{iDisplayStart},#{iDisplayLength}

SELECT count(*) FROM pub_coooperate t LEFT JOIN pub_lease_organ_relation t1 ON (t1.`status` = 1 AND t1.companyid = t.companyid AND t1.leasecompanyid = t.leasecompanyid AND t1.organid = #{key}) LEFT JOIN le_leasescompany t2 ON (t.leasecompanyid = t2.id AND t2.`Status` = 1) WHERE 1 = 1 AND t.servicetype = 0 AND t.platformtype = 1 AND t.`status` = 1 AND t.companyid = #{leasescompanyid}
and t.id = #{queryForTheCarBody}
AND t1.Id IS NULL

SELECT t.id as id,t2.shortname as text FROM pub_coooperate t LEFT JOIN pub_lease_organ_relation t1 ON (t1.`status` = 1 AND t1.companyid = t.companyid AND t1.leasecompanyid = t.leasecompanyid AND t1.organid = #{key}) LEFT JOIN le_leasescompany t2 ON (t.leasecompanyid = t2.id AND t2.`Status` = 1) WHERE 1 = 1 AND t.servicetype = 0 AND t.platformtype = 1 AND t.`status` = 1 AND t.companyid = #{leasescompanyid}
and t2.shortname like "%"#{queryForTheCarBody}"%"
AND t1.Id IS NULL
