mapper.select:
select t.* from (select (@rownum := @rownum + 1) as rownum,t1.* from (select org_organ.shortname as shortName,org_organ.city as city,org_organ_company_ref.actualbalance as actualBalance,org_organ_company_ref.balance as balance,org_organ_company_ref.lineofcredit as lineOfCredit,org_organ.fullname as fullName,org_organ_company_ref.id as id,org_organ.id as organId, pub_cityaddr.city as cityName,(org_organ_company_ref.actualbalance + org_organ_company_ref.lineofcredit + IFNULL(org_organ_company_ref.couponamount,0) - org_organ_company_ref.balance) as unbalance, IFNULL(org_organ_company_ref.couponamount,0) as couponamount from org_organ_company_ref inner join org_organ on (org_organ_company_ref.organid = org_organ.id) left join pub_cityaddr on (pub_cityaddr.id = org_organ.city and pub_cityaddr.status = 1) where org_organ.status = 1 and org_organ_company_ref.companyid = #{leasesCompanyId} and org_organ_company_ref.status = 1
and org_organ.city = #{city}
and org_organ_company_ref.organid = #{organId}
and org_organ_company_ref.organid IN ( SELECT le_roledataauthority.dynamicid as organid FROM le_roledataauthority, le_roleuser, le_user WHERE le_roledataauthority.status=1 and le_roleuser.status=1 and le_user.status=1 and le_user.id = le_roleuser.userid and le_roleuser.roleid = le_roledataauthority.roleid and le_user.account=#{account} and le_user.leasescompanyid = #{leasesCompanyId} )
and (org_organ_company_ref.actualbalance + org_organ_company_ref.lineofcredit + IFNULL(org_organ_company_ref.couponamount,0) - org_organ_company_ref.balance) > #{unbalance}
order by pub_cityaddr.cityInitials,org_organ_company_ref.actualbalance) t1, (select @rownum := 0) r ) t
where t.rownum > #{iDisplayStart} and t.rownum <= (#{iDisplayStart} + #{iDisplayLength})

	    select count(*)
	    from org_organ_company_ref inner join org_organ on (org_organ_company_ref.organid = org_organ.id)
	    where org_organ.status = 1
	      and org_organ_company_ref.companyid = #{leasesCompanyId}
	      and org_organ_company_ref.status = 1

	        and org_organ.city = #{city}


	        and org_organ_company_ref.organid = #{organId}





              and org_organ_company_ref.organid IN (
              SELECT
			        le_roledataauthority.dynamicid as organid
		      FROM
			        le_roledataauthority,
			        le_roleuser,
			        le_user
		      WHERE
		            le_roledataauthority.status=1
		            and le_roleuser.status=1
		            and le_user.status=1
		            and le_user.id = le_roleuser.userid
		            and le_roleuser.roleid = le_roledataauthority.roleid
		            and le_user.account=#{account}
		            and le_user.leasescompanyid = #{leasesCompanyId}
		      )







	        and (org_organ_company_ref.actualbalance + org_organ_company_ref.lineofcredit + IFNULL(org_organ_company_ref.couponamount,0) - org_organ_company_ref.balance) > #{unbalance}

select count(*)
from org_organexpenses
where org_organexpenses.organid = #{organId}
  and org_organexpenses.leasescompanyid = #{leasesCompanyId}
  and org_organexpenses.status = 1

    and org_organexpenses.type = #{type}


    and org_organexpenses.expensetime  >=  STR_TO_DATE(#{startTime},'%Y-%m-%d %H:%i:%s')


    and org_organexpenses.expensetime  <  STR_TO_DATE(ADDDATE(#{endTime},1),'%Y-%m-%d %H:%i:%s')

mapper.insert:
	    insert into org_organexpenses(id,organid,leasescompanyid,type,expensetime,amount,balance,remark,createtime,updatetime,creater,updater,status,operatortype,operateresult)
        select #{id},#{organId},#{leasesCompanyId},#{type},now(),#{amount},IFNULL(actualbalance,0),#{remark},now(),now(),#{creater},#{updater},1,0,#{operateResult} 
        from org_organ_company_ref 
        where organid = #{organId} 
          and companyid = #{leasesCompanyId} 
          and status = 1

choose.otherwise:
SELECT org_organ.id as id,org_organ.shortname as text FROM org_organ, org_organ_company_ref WHERE org_organ.status=1 and org_organ_company_ref.status=1 and org_organ_company_ref.organid = org_organ.id and org_organ_company_ref.companyid = #{leasesCompanyId}
and org_organ.shortname like "%"#{shortName}"%"
group by org_organ.id

mapper.select:
select count(*)
from pub_coupon_detail
where pub_coupon_detail.userid = #{organId}
  and pub_coupon_detail.lecompanyid = #{leasesCompanyId}
  and pub_coupon_detail.status = 1

    and pub_coupon_detail.usetype = #{type}


    and pub_coupon_detail.usetype in (1,2,3,4,5)


    and pub_coupon_detail.createtime  >=  STR_TO_DATE(#{startTime},'%Y-%m-%d %H:%i:%s')


    and pub_coupon_detail.createtime  <  STR_TO_DATE(ADDDATE(#{endTime},1),'%Y-%m-%d %H:%i:%s')

select pub_coupon_detail.createtime,pub_coupon_detail.usetype,pub_coupon_detail.amount,pub_coupon_detail.balance,pub_coupon_detail.remark
from pub_coupon_detail
where pub_coupon_detail.userid = #{organId}
  and pub_coupon_detail.lecompanyid = #{leasesCompanyId}
  and pub_coupon_detail.status = 1

    and pub_coupon_detail.usetype = #{type}


    and pub_coupon_detail.usetype in (1,2,3,4,5)


    and pub_coupon_detail.createtime  >=  STR_TO_DATE(#{startTime},'%Y-%m-%d %H:%i:%s')


    and pub_coupon_detail.createtime  <  STR_TO_DATE(ADDDATE(#{endTime},1),'%Y-%m-%d %H:%i:%s')

order by pub_coupon_detail.createtime desc

mapper.update:
update org_organ_company_ref
set balance=IFNULL(balance,0) + #{money},couponamount=IFNULL(couponamount,0) + #{money},updatetime=now()
where organid = #{organid}
  and companyid = #{companyid}
  and status = 1

mapper.insert:
	    insert into pub_coupon_detail(id,userid,couponidref,usetype,amount,balance,remark,platformtype,lecompanyid,updatetime,createtime,status)
	    select #{id},#{organid},#{couponidref},#{usetype},#{money},couponamount,#{remark},1,#{companyid},now(),now(),1
	    from org_organ_company_ref 
        where organid = #{organid} 
          and companyid = #{companyid} 
          and status = 1

mapper.update:
update org_organ_company_ref
set balance=IFNULL(balance,0) - IFNULL(couponamount,0),couponamount=0,updatetime=now()
where organid = #{organid}
  and companyid = #{companyid}
  and status = 1

mapper.select:
select IFNULL(couponamount,0) 
from org_organ_company_ref 
where organid = #{organid}
  and companyid = #{companyid}
  and status = 1

mapper.insert:
	    insert into pub_coupon_detail(id,userid,couponidref,usetype,amount,balance,remark,platformtype,lecompanyid,updatetime,createtime,status)
	    select #{id},#{organid},#{couponidref},#{usetype},couponamount,0,#{remark},1,#{companyid},now(),now(),1
	    from org_organ_company_ref 
        where organid = #{organid} 
          and companyid = #{companyid} 
          and status = 1
