mapper.select:
select t.* from (select (@rownum := @rownum + 1) as rownum,t1.* from (select org_organbill.id as id,org_organbill.organid as organId,org_organbill.source as source, (case org_organbill.source when '0' then '月结' when '1' then '季结' when '2' then '手动结算' end) as sourceName, org_organbill.name as name,org_organbill.money as money,org_organbill.createtime as createTime, org_organ.shortname as shortName, t2.billstate as billState,t2.operationtime as operationTime, (case t2.billstate when '2' then '待核对' when '3' then '待机构核对' when '4' then '待机构付款' when '5' then '机构退回账单' when '6' then '机构已付款' when '7' then '已结算' when '8' then '已作废' end) as billStateName, (select count(*) from org_organbilldetails where org_organbilldetails.billsid = org_organbill.id and org_organbilldetails.status = 1) as orderCount from org_organbill,org_organ, (select t1.billsid,t1.operationtime,t1.billstate from ( select org_organbillstate.billsid,org_organbillstate.operationtime,org_organbillstate.billstate from org_organbillstate where org_organbillstate.status = 1 order by org_organbillstate.operationtime desc ) t1 group by t1.billsid ) t2 where org_organbill.id = t2.billsid and org_organbill.organid = org_organ.id and org_organbill.leasescompanyid = #{leasesCompanyId} and org_organbill.status = 1
and org_organbill.organid = #{organId}
and org_organbill.organid IN (
SELECT le_roledataauthority.dynamicid as organid FROM le_roledataauthority, le_roleuser, le_user WHERE le_roledataauthority.status=1 and le_roleuser.status=1 and le_user.status=1 and le_user.id = le_roleuser.userid and le_roleuser.roleid = le_roledataauthority.roleid and le_user.account=#{account} and le_user.leasescompanyid = #{leasesCompanyId}
SELECT org_organ.id as organid FROM org_organ, org_organ_company_ref WHERE org_organ.status=1 and org_organ_company_ref.status=1 and org_organ_company_ref.organid = org_organ.id and org_organ_company_ref.companyid = #{leasesCompanyId}
)
and org_organbill.createtime >= STR_TO_DATE(#{startTime},'%Y-%m-%d %H:%i:%s')
and org_organbill.createtime < STR_TO_DATE(ADDDATE(#{endTime},1),'%Y-%m-%d %H:%i:%s')
and (t2.billstate = 2 or t2.billstate = 3 or t2.billstate = 4 or t2.billstate = 5)
and (t2.billstate = 7 or t2.billstate = 8)
and t2.billstate = #{billState}
order by t2.operationtime desc ) t1, (select @rownum := 0) r ) t
where t.rownum > #{iDisplayStart} and t.rownum <= (#{iDisplayStart} + #{iDisplayLength})

select count(*) from org_organbill,org_organ, (select t1.billsid,t1.operationtime,t1.billstate from ( select org_organbillstate.billsid,org_organbillstate.operationtime,org_organbillstate.billstate from org_organbillstate where org_organbillstate.status = 1 order by org_organbillstate.operationtime desc ) t1 group by t1.billsid ) t2 where org_organbill.id = t2.billsid and org_organbill.organid = org_organ.id and org_organbill.leasescompanyid = #{leasesCompanyId} and org_organbill.status = 1
and org_organbill.organid = #{organId}
and org_organbill.organid IN (
SELECT le_roledataauthority.dynamicid as organid FROM le_roledataauthority, le_roleuser, le_user WHERE le_roledataauthority.status=1 and le_roleuser.status=1 and le_user.status=1 and le_user.id = le_roleuser.userid and le_roleuser.roleid = le_roledataauthority.roleid and le_user.account=#{account} and le_user.leasescompanyid = #{leasesCompanyId}
SELECT org_organ.id as organid FROM org_organ, org_organ_company_ref WHERE org_organ.status=1 and org_organ_company_ref.status=1 and org_organ_company_ref.organid = org_organ.id and org_organ_company_ref.companyid = #{leasesCompanyId}
)
and org_organbill.createtime >= STR_TO_DATE(#{startTime},'%Y-%m-%d %H:%i:%s')
and org_organbill.createtime < STR_TO_DATE(ADDDATE(#{endTime},1),'%Y-%m-%d %H:%i:%s')
and (t2.billstate = 2 or t2.billstate = 3 or t2.billstate = 4 or t2.billstate = 5)
and (t2.billstate = 7 or t2.billstate = 8)
and t2.billstate = #{billState}

select org_organbill.id as id,org_organbill.organid as organId,org_organbill.source as source, (case org_organbill.source when '0' then '月结' when '1' then '季结' when '2' then '手动结算' end) as sourceName, org_organbill.name as name,org_organbill.money as money,org_organbill.createtime as createTime, org_organ.shortname as shortName, t2.billstate as billState,t2.operationtime as operationTime, (case t2.billstate when '2' then '待核对' when '3' then '待机构核对' when '4' then '待机构付款' when '5' then '机构退回账单' when '6' then '机构已付款' when '7' then '已结算' when '8' then '已作废' end) as billStateName from org_organbill,org_organ, (select t1.billsid,t1.operationtime,t1.billstate from ( select org_organbillstate.billsid,org_organbillstate.operationtime,org_organbillstate.billstate from org_organbillstate where org_organbillstate.status = 1 order by org_organbillstate.operationtime desc ) t1 group by t1.billsid ) t2 where org_organbill.id = t2.billsid and org_organbill.organid = org_organ.id and org_organbill.leasescompanyid = #{leasesCompanyId} and org_organbill.status = 1
and org_organbill.organid = #{organId}
and org_organbill.organid IN (
SELECT le_roledataauthority.dynamicid as organid FROM le_roledataauthority, le_roleuser, le_user WHERE le_roledataauthority.status=1 and le_roleuser.status=1 and le_user.status=1 and le_user.id = le_roleuser.userid and le_roleuser.roleid = le_roledataauthority.roleid and le_user.account=#{account} and le_user.leasescompanyid = #{leasesCompanyId}
SELECT org_organ.id as organid FROM org_organ, org_organ_company_ref WHERE org_organ.status=1 and org_organ_company_ref.status=1 and org_organ_company_ref.organid = org_organ.id and org_organ_company_ref.companyid = #{leasesCompanyId}
)
and org_organbill.createtime >= STR_TO_DATE(#{startTime},'%Y-%m-%d %H:%i:%s')
and org_organbill.createtime < STR_TO_DATE(ADDDATE(#{endTime},1),'%Y-%m-%d %H:%i:%s')
and (t2.billstate = 2 or t2.billstate = 3 or t2.billstate = 4 or t2.billstate = 5)
and (t2.billstate = 7 or t2.billstate = 8)
and t2.billstate = #{billState}
order by t2.operationtime desc

select count(*) from org_order where org_order.companyid = #{leasesCompanyId} and org_order.usetype = 0 and org_order.paymethod = 2 and org_order.status = 1 and (org_order.orderstatus = 7 or org_order.orderstatus = 8) and org_order.paymentstatus = 4
and org_order.organid IN ( select organ.id from org_organ organ where organ.status = 1 and organ.creditcode = ( select org_organ.creditcode from org_organ where org_organ.status = 1 and org_organ.id = #{organId} ) )
and org_order.endtime >= STR_TO_DATE(#{startTime},'%Y-%m-%d %H:%i:%s')
and org_order.endtime < STR_TO_DATE(ADDDATE(#{endTime},1),'%Y-%m-%d %H:%i:%s')
and org_order.reviewstatus = #{billState}
and (org_order.reviewstatus = 0 or org_order.reviewstatus = 2)
and org_order.expensetype = #{expensetype}
and NOT EXISTS ( select * from (select org_organbilldetails.orderid from org_organbill inner join (select t1.billsid,t1.operationtime,t1.billstate from ( select org_organbillstate.billsid,org_organbillstate.operationtime,org_organbillstate.billstate from org_organbillstate where org_organbillstate.status = 1 order by org_organbillstate.operationtime desc ) t1 group by t1.billsid ) t2 on org_organbill.id = t2.billsid inner join org_organbilldetails on org_organbill.id = org_organbilldetails.billsid where org_organbill.leasescompanyid = #{leasesCompanyId} and org_organbill.status = 1
and org_organbill.organid = #{organId}
and (t2.billstate = 1 or t2.billstate = 2 or t2.billstate = 3 or t2.billstate = 4 or t2.billstate = 5 or t2.billstate = 6) and org_organbilldetails.status = 1) t3 where org_order.orderno = t3.orderid )


select actualbalance
from org_organ_company_ref
where organid = #{organId}
  and companyid = #{leasesCompanyId}
  and status = 1

mapper.update:
	update org_organ_company_ref
	set actualbalance=IFNULL(actualbalance,0) - #{money},updatetime=now()
	where organid = #{organId}
      and companyid = #{leasesCompanyId}
      and status = 1

choose.otherwise:
SELECT org_organ.id as id,org_organ.fullname as text,org_organ.initials as initials FROM org_organ, org_organ_company_ref WHERE org_organ.status=1 and org_organ_company_ref.status=1 and org_organ_company_ref.organid = org_organ.id and org_organ_company_ref.companyid = #{leasesCompanyId}
and org_organ.fullname like "%"#{fullName}"%"
group by org_organ.id order by org_organ.initials

mapper.select:
	    select count(*)
        from org_organbill inner join
	      (select t1.billsid,t1.operationtime,t1.billstate from (
	      select org_organbillstate.billsid,org_organbillstate.operationtime,org_organbillstate.billstate
	      from org_organbillstate
	      where org_organbillstate.status = 1
          order by org_organbillstate.operationtime desc  
	      ) t1 group by t1.billsid 
	      ) t2 on org_organbill.id = t2.billsid
        where org_organbill.leasescompanyid = #{leasesCompanyId}
          and org_organbill.status = 1
          and org_organbill.organid = #{organId}
          and t2.billstate in ('1','2','3','4','5','6','9')

select org_organ_company_ref.*
from org_organ_company_ref
where org_organ_company_ref.organid = #{organId} 
  and org_organ_company_ref.companyid = #{leasesCompanyId}
  and org_organ_company_ref.status = 1

mapper.update:
	update org_organ_company_ref
	set lineofcredit = #{lineOfCredit}, balance = #{balance} , updatetime = now()
	where org_organ_company_ref.organid = #{organId} 
      and org_organ_company_ref.companyid = #{leasesCompanyId}
      and org_organ_company_ref.status = 1
