mapper.select:
select count(*) from org_user inner join ( select org_user.id,org_user.account from org_user inner join ( select org_organ.id,t2.shortname from org_organ,(
select org_organ.creditcode,org_organ.shortname from org_organ, ( SELECT le_roledataauthority.dynamicid as organid FROM le_roledataauthority, le_roleuser, le_user WHERE le_roledataauthority.status=1 and le_roleuser.status=1 and le_user.status=1 and le_user.id = le_roleuser.userid and le_roleuser.roleid = le_roledataauthority.roleid and le_user.account=#{account} and le_user.leasescompanyid = #{leasesCompanyId} ) t4 where org_organ.status = 1 and org_organ.id = t4.organid
select org_organ.creditcode,org_organ.shortname from org_organ_company_ref,org_organ where org_organ_company_ref.companyid = #{leasesCompanyId} and org_organ_company_ref.status = 1 and org_organ.status = 1 and org_organ_company_ref.organid = org_organ.id
) t2 where org_organ.status = 1 and org_organ.creditcode = t2.creditcode ) t3 on (org_user.organid = t3.id)
where org_user.status = 1 and org_user.usertype = 0
and org_user.organid IN ( select organ.id from org_organ organ where organ.status = 1 and organ.creditcode = ( select org_organ.creditcode from org_organ where org_organ.status = 1 and org_organ.id = #{organId} ) )
UNION select org_user.id,org_user.account from org_user, (select t6.account,t6.id,t6.createtime from (select org_user.account,org_user.id,org_useraccount.createtime from org_user,org_useraccount where org_user.id = org_useraccount.userid and org_useraccount.leasescompanyid = #{leasesCompanyId} and org_useraccount.status = 1 order by org_useraccount.createtime desc) t6 group by t6.account) t7, (select org_user.account,t3.shortname from org_user inner join ( select org_organ.id,t2.shortname from org_organ,(
select org_organ.creditcode,org_organ.shortname from org_organ, ( SELECT le_roledataauthority.dynamicid as organid FROM le_roledataauthority, le_roleuser, le_user WHERE le_roledataauthority.status=1 and le_roleuser.status=1 and le_user.status=1 and le_user.id = le_roleuser.userid and le_roleuser.roleid = le_roledataauthority.roleid and le_user.account=#{account} and le_user.leasescompanyid = #{leasesCompanyId} ) t4 where org_organ.status = 1 and org_organ.id = t4.organid
select org_organ.creditcode,org_organ.shortname from org_organ_company_ref,org_organ where org_organ_company_ref.companyid = #{leasesCompanyId} and org_organ_company_ref.status = 1 and org_organ.status = 1 and org_organ_company_ref.organid = org_organ.id
) t2 where org_organ.status = 1 and org_organ.creditcode = t2.creditcode ) t3 on (org_user.organid = t3.id) where org_user.status != 1 and org_user.usertype = 0 ) t5 where org_user.usertype = 0 and t7.account = t5.account and org_user.id = t7.id
) b1 on (org_user.id = b1.id) where org_user.usertype = 0
and org_user.id = #{userId}

select t.* from (select (@rownum := @rownum + 1) as rownum,t1.* from (select org_userexpenses.expensetime as expenseTime, (case org_userexpenses.tradetype when '0' then '充值' when '1' then '订单支付' when '2' then '系统退款' when '3' then '提现' end) as tradeType, (case org_userexpenses.expensetype when '1' then '微信支付' when '2' then '支付宝支付' when '3' then '账户余额' when '4' then '余额支付' when '5' then '付现' else '/' end) as expenseType, CONCAT((case org_userexpenses.tradetype when '0' then '+' when '1' then '-' when '2' then '+' when '3' then (case org_userexpenses.operateresult when '0' then '-' when '1' then '+' end) end),org_userexpenses.amount) as amount, IFNULL(org_userexpenses.balance,0) as balance, IFNULL(org_userexpenses.remark,'无') as remark from org_userexpenses where org_userexpenses.leasescompanyid = #{leasesCompanyId} and org_userexpenses.userid = #{userId}
and org_userexpenses.tradetype = #{expenseType}
and org_userexpenses.expensetime >= STR_TO_DATE(#{startTime},'%Y-%m-%d %H:%i:%s')
and org_userexpenses.expensetime < STR_TO_DATE(ADDDATE(#{endTime},1),'%Y-%m-%d %H:%i:%s')
and org_userexpenses.detailtype = #{detailType}
order by org_userexpenses.expensetime desc) t1, (select @rownum := 0) r ) t
where t.rownum > #{iDisplayStart} and t.rownum <= (#{iDisplayStart} + #{iDisplayLength})

select count(*)
from org_userexpenses
where org_userexpenses.leasescompanyid = #{leasesCompanyId}
  and org_userexpenses.userid = #{userId}

    and org_userexpenses.tradetype = #{expenseType}


    and org_userexpenses.expensetime  >=  STR_TO_DATE(#{startTime},'%Y-%m-%d %H:%i:%s')


    and org_userexpenses.expensetime  <  STR_TO_DATE(ADDDATE(#{endTime},1),'%Y-%m-%d %H:%i:%s')


    and org_userexpenses.detailtype = #{detailType}

choose.otherwise:
SELECT org_organ.id as id,org_organ.shortname as text FROM org_organ, org_organ_company_ref WHERE org_organ.status=1 and org_organ_company_ref.status=1 and org_organ_company_ref.organid = org_organ.id and org_organ_company_ref.companyid = #{leasesCompanyId}
and org_organ.shortname like "%"#{shortName}"%"
group by org_organ.id

mapper.select:
select org_userexpenses.expensetime as expenseTime, (case org_userexpenses.tradetype when '0' then '充值' when '1' then '订单支付' when '2' then '系统退款' when '3' then '提现' end) as tradeType, (case org_userexpenses.expensetype when '1' then '微信支付' when '2' then '支付宝支付' when '3' then '账户余额' when '4' then '余额支付' when '5' then '付现' else '/' end) as expenseType, CONCAT((case org_userexpenses.tradetype when '0' then '+' when '1' then '-' when '2' then '+' when '3' then (case org_userexpenses.operateresult when '0' then '-' when '1' then '+' end) end),org_userexpenses.amount) as amount, IFNULL(org_userexpenses.balance,0) as balance, IFNULL(org_userexpenses.remark,'无') as remark from org_userexpenses where org_userexpenses.leasescompanyid = #{leasesCompanyId} and org_userexpenses.userid = #{userId}
and org_userexpenses.tradetype = #{expenseType}
and org_userexpenses.expensetime >= STR_TO_DATE(#{startTime},'%Y-%m-%d %H:%i:%s')
and org_userexpenses.expensetime < STR_TO_DATE(ADDDATE(#{endTime},1),'%Y-%m-%d %H:%i:%s')
and org_userexpenses.detailtype = #{detailType}
order by org_userexpenses.expensetime desc

select t.* from (select (@rownum := @rownum + 1) as rownum,t1.* from (select pc.couponactivyidref as id, pc.name, pc.couponstatus, DATE_FORMAT(pc.createtime,'%Y-%m-%d %H:%i') as sendtime, pc.money as amount, DATE_FORMAT(pc.outimestart,'%Y.%m.%d') as usestarttime, DATE_FORMAT(pc.outtimeend,'%Y.%m.%d') useendtime, GROUP_CONCAT(city.city SEPARATOR ',') as usecity from pub_coupon pc left join pub_coupon_activity_use_city pcauc on pc.couponactivyidref=pcauc.couponactivityidref left join pub_cityaddr city on city.id=pcauc.city where pc.lecompanyid=#{lecompanyid} and pc.platformtype=1 and pc.target=2 and pc.userid=#{userid}
and pc.couponstatus=#{couponstatus}
and pc.createtime >= STR_TO_DATE(#{starttime},'%Y-%m-%d %H:%i:%s')
and pc.createtime < STR_TO_DATE(ADDDATE(#{endtime},1),'%Y-%m-%d %H:%i:%s')
group by pc.id) t1, (select @rownum := 0) r ) t
where t.rownum > #{iDisplayStart} and t.rownum <= (#{iDisplayStart} + #{iDisplayLength})

select count(*) from pub_coupon pc
where pc.lecompanyid=#{lecompanyid} and pc.platformtype=1 and pc.target=2 and pc.userid=#{userid}
and pc.couponstatus=#{couponstatus}
and pc.createtime >= STR_TO_DATE(#{starttime},'%Y-%m-%d %H:%i:%s')
and pc.createtime < STR_TO_DATE(ADDDATE(#{endtime},1),'%Y-%m-%d %H:%i:%s')

select pc.couponactivyidref as id, pc.name, pc.couponstatus, DATE_FORMAT(pc.createtime,'%Y-%m-%d %H:%i') as sendtime, pc.money as amount, DATE_FORMAT(pc.outimestart,'%Y.%m.%d') as usestarttime, DATE_FORMAT(pc.outtimeend,'%Y.%m.%d') useendtime, GROUP_CONCAT(city.city SEPARATOR ',') as usecity from pub_coupon pc left join pub_coupon_activity_use_city pcauc on pc.couponactivyidref=pcauc.couponactivityidref left join pub_cityaddr city on city.id=pcauc.city where pc.lecompanyid=#{lecompanyid} and pc.platformtype=1 and pc.target=2 and pc.userid=#{userid}
and pc.couponstatus=#{couponstatus}
and pc.createtime >= STR_TO_DATE(#{starttime},'%Y-%m-%d %H:%i:%s')
and pc.createtime < STR_TO_DATE(ADDDATE(#{endtime},1),'%Y-%m-%d %H:%i:%s')
group by pc.id

