mapper.select:
select t1.id, le.name leasecompanyText, coono, cootype, ifnull((select count(1) from pub_cooresource t2 where t1.id=t2.coooperateid and t2.status=1), 0) openresource, servicetype, coostate, case when ifnull(coostate,2)=0 or ifnull(coostate,2) =2 then '/' else concat(DATE_FORMAT(t1.coostarttime, '%Y.%m.%d'),'-',DATE_FORMAT(t1.cooendtime, '%Y.%m.%d')) end validatetime, DATE_FORMAT(reviewtime, '%Y-%m-%d') reviewtime, case when ifnull(coostate,2)=2 then reviewtext else '/' end reviewtext, DATE_FORMAT(applicationtime, '%Y-%m-%d') applicationtime from pub_coooperate t1 inner join le_leasescompany le on le.id=t1.leasecompanyid where 1=1 and t1.companyid = #{companyid} and t1.status=1
and instr(t1.coono, #{coono}) > 0
and t1.leasecompanyid = #{leasecompanyid}
and t1.servicetype = #{servicetype}
and t1.coostate = #{coostate}
and t1.applicationtime >= #{applicationtimeStart}
and t1.applicationtime < #{applicationtimeEnd}
order by applicationtime desc

select distinct coo.leasecompanyid id, le.shortname text from pub_coooperate coo inner join le_leasescompany le on coo.leasecompanyid=le.id where coo.status=1 and coo.companyid = #{companyid}
and instr(shortname, #{keyword}) > 0
order by le.name asc

mapper.update:
update pub_coooperate
set coostate=#{coostate},
updater=#{updater},
updatetime=#{updatetime},
cooendtime=#{cooendtime}
where id=#{id}
