mapper.select:
select
    t1.id,
    le.name leasecompanyText,
    coono,
    cootype,
    servicetype,
    coostate,
    case when ifnull(coostate,2)=0 or ifnull(coostate,2) =2 then '/'
    else concat(DATE_FORMAT(t1.coostarttime, '%Y.%m.%d'),'-',DATE_FORMAT(t1.cooendtime, '%Y.%m.%d')) end validatetime,
    DATE_FORMAT(applicationtime, '%Y-%m-%d') applicationtime
from pub_coooperate t1
inner join le_leasescompany le on le.id=t1.companyid
where 1=1
    and t1.leasecompanyid = #{companyid}
    and t1.status=1

        and instr(t1.coono, #{coono}) > 0


        and t1.companyid = #{leasecompanyid}


        and t1.servicetype = #{servicetype}


        and t1.coostate = #{coostate}


        and t1.cootype = #{cootype}


        and t1.applicationtime >= #{applicationtimeStart}


        and t1.applicationtime < #{applicationtimeEnd}

    order by t1.applicationtime desc

select distinct coo.coono id, coo.coono text from pub_coooperate coo where coo.status=1 and coo.leasecompanyid =#{key}
and instr(coono, #{sSearch}) > 0
order by coo.coono asc

select distinct coo.companyid id, le.name text from pub_coooperate coo inner join le_leasescompany le on coo.companyid=le.id where coo.status=1 and coo.leasecompanyid = #{key}
and instr(le.name, #{sSearch}) > 0
order by le.name asc

mapper.update:
update pub_coooperate
set coostate=#{coostate},
updater=#{updater},
updatetime=#{updatetime},
cooendtime=#{cooendtime}
where id=#{id}

mapper.select:
select * from ( select cooo.id coooid, ve.fullplateno, -- 车牌号 concat(br.name, ' ', li.name) vehclinename, -- 品牌车系 li.id vehclineid, d.jobnum, -- 资格证号 concat(d.name, ' ', d.phone) driverInfo, -- 司机信息 ifnull(d.workstatus, '/') workstatus, -- 服务状态 concat(mo.name,'(', ve.loads, '座)') vehiclemodelsname, -- 服务车型 mo.id vehiclemodels, ci.city cityname, case d.workstatus when null or '' then 0 when 2 then 1 when 0 then 2 when 1 then 3 else 0 end workOrder, case when cooc.id is null then 0 else 1 end resource, ci.fullpinyin, ve.id vehicleid, ci.id cityaddr, mo.level from pub_coooperate cooo inner join pub_vehicle ve on ve.leasescompanyid=cooo.leasecompanyid and ve.vehicletype=cooo.servicetype and ve.status=1 inner join pub_vehcline li on li.id=ve.vehclineid inner join pub_vehcbrand br on br.id=li.vehcBrandID left join (select distinct driverid,vehicleid from pub_driver_vehicle_ref vreft inner join pub_driver dt on dt.id=vreft.driverid and vreft.status=1 AND ( (dt.vehicletype = '1' AND dt.passworkstatus IN( '0' ,'1' ,'3') ) OR (dt.vehicletype = '0') ) ) vref on vref.vehicleid=ve.id left join pub_driver d on d.id=vref.driverid left join le_vehcline_models_ref mref on mref.vehclineid=li.id and mref.status=1 left join le_vehiclemodels mo on mo.Id=mref.vehiclemodelsid inner join pub_cityaddr ci on ci.id=ve.city left join pub_cooresource cooc on cooo.id=cooc.coooperateid and cooc.vehicleid=ve.id where cooo.id=#{coooid} ) t where 1=1
and resource='1'
and ((resource='0' and driverInfo != '') or resource='1' )
and vehclineid=#{vehclineid}
and vehiclemodels=#{vehiclemodels}
and workstatus=#{workstatus}
and cityaddr=#{cityaddrid}
and instr(jobnum, #{jobnum}) > 0
and instr(fullplateno, #{fullplateno}) > 0
order by fullpinyin,level,workOrder,fullplateno asc

select * from ( select distinct concat(br.name, '.', li.name) text, -- 品牌车系 li.id id, cooo.id coooid from pub_coooperate cooo inner join pub_vehcline li on li.leasescompanyid=cooo.leasecompanyid inner join pub_vehcbrand br on br.id=li.vehcBrandID ) t where 1=1 and coooid=#{coooId}
and instr(text, #{keyword}) > 0
order by text asc

select * from ( select ve.fullplateno, -- 车牌号 concat(br.name, ' ', li.name) vehclinename, -- 品牌车系 li.id vehclineid, d.jobnum, -- 资格证号 concat(d.name, ' ', d.phone) driverInfo, -- 司机信息 ifnull(d.workstatus, '/') workstatus, -- 服务状态 concat(mo.name,'(', ve.loads, '座)') vehiclemodelsname, -- 服务车型 mo.id vehiclemodels, ci.city cityname, case d.workstatus when null then 0 when 2 then 1 when 0 then 2 when 1 then 3 else 0 end workOrder, ci.fullpinyin, ve.id vehicleid, ci.id cityaddr, mo.level from pub_vehicle ve inner join pub_vehcline li on li.id=ve.vehclineid inner join pub_vehcbrand br on br.id=li.vehcBrandID left join (select distinct driverid,vehicleid from pub_driver_vehicle_ref vreft inner join pub_driver dt on dt.id=vreft.driverid and vreft.status=1 AND ( (dt.vehicletype = '1' AND dt.passworkstatus IN( '0' ,'1' ,'3') ) OR (dt.vehicletype = '0') ) ) vref on vref.vehicleid=ve.id left join pub_driver d on d.id=vref.driverid left join le_vehcline_models_ref mref on mref.vehclineid=li.id and mref.status=1 left join le_vehiclemodels mo on mo.Id=mref.vehiclemodelsid inner join pub_cityaddr ci on ci.id=ve.city where ve.status=1 and ve.leasescompanyid=#{leasescompanyid} and ve.vehicletype=#{vehicletype} ) t where 1=1 and driverInfo is not null and driverInfo != ''
and vehclineid=#{vehclineid}
and vehiclemodels=#{vehiclemodels}
and workstatus=#{workstatus}
and cityaddr=#{cityaddrid}
and instr(jobnum, #{jobnum}) > 0
and instr(fullplateno, #{fullplateno}) > 0
order by fullpinyin,workOrder,level,fullplateno asc
