mapper.select:
select t.* from (select (@rownum := @rownum + 1) as rownum,t1.* from (select pub_coupon_rule.id,pub_coupon_rule.name,pub_coupon_rule.ruletarget,pub_coupon_rule.ruletype,pub_coupon_rule.rulecontent,pub_coupon_rule.createtime,pub_coupon_rule.updatetime, (select count(*) from pub_coupon_rule_history where pub_coupon_rule_history.ruleidref = pub_coupon_rule.id and pub_coupon_rule_history.platformtype = 1 and pub_coupon_rule_history.status = 1) as historycount from pub_coupon_rule where pub_coupon_rule.status = 1 and pub_coupon_rule.platformtype = 1
and pub_coupon_rule.ruletype = #{ruletype}
and pub_coupon_rule.lecompanyid = #{lecompanyid}
and pub_coupon_rule.ruletarget = #{ruletarget}
order by pub_coupon_rule.createtime desc) t1, (select @rownum := 0) r ) t
where t.rownum > #{iDisplayStart} and t.rownum <= (#{iDisplayStart} + #{iDisplayLength})

select count(*)
from pub_coupon_rule
where pub_coupon_rule.status = 1
  and pub_coupon_rule.platformtype = 1

    and pub_coupon_rule.ruletype = #{ruletype}


    and pub_coupon_rule.lecompanyid = #{lecompanyid} 


    and pub_coupon_rule.ruletarget = #{ruletarget}

mapper.update:
update pub_coupon_rule 
set updatetime = now()
  , updater = #{updater}

	, name = #{name} 


	, ruletype = #{ruletype} 


	, ruletarget = #{ruletarget} 


	, rechargemoney = #{rechargemoney} 


	, consumetype = #{consumetype} 

	, cycleday = #{cycleday} 

	, consumefrequencytype = #{consumefrequencytype} 

	, consumehightimes = #{consumehightimes} 
	, consumelowtimes = #{consumelowtimes} 

	, consumemoneysingleable = #{consumemoneysingleable} 

	, consumemoneysingelfull = #{consumemoneysingelfull} 

	, consumemoneycycleable = #{consumemoneycycleable} 


	, consumemoneycycletype = #{consumemoneycycletype} 

	, consumemoneycyclefull = #{consumemoneycyclefull} 
	, consumemoneycyclelow = #{consumemoneycyclelow} 

	, rulecontent = #{rulecontent} 


	, lecompanyid = #{lecompanyid} 

where id = #{id}
