mapper.select:
SELECT
COUNT(*)
FROM
pub_driver
WHERE
pub_driver.`Status` = 1
AND (
jobnum = #{jobNum}
or driversnum =
#{driversNum}
or idcardnum = #{idCardNum}
)
and id != #{id}
and leasescompanyid=#{leasesCompanyId}

and pub_driver.platformtype=1

SELECT t.* FROM ( SELECT (@rownum := @rownum + 1) AS indexnum, m.* FROM ( SELECT *, (SELECT pub_cityaddr.city FROM pub_cityaddr WHERE pub_cityaddr.`status`=1 AND pub_cityaddr.id = pub_driver.city) AS cityName,
(SELECT text FROM pub_dictionary where pub_dictionary.Type='服务状态' and pub_dictionary.`Value`=pub_driver.workstatus and pub_dictionary.status=1)as workStatusName, (CASE workstatus WHEN 2 THEN 1 WHEN 0 THEN 2 WHEN 1 THEN 3 ELSE 0 END) FROM pub_driver, (SELECT @rownum := 0) r WHERE pub_driver.`Status` = 1 AND pub_driver.leasescompanyid= #{leasesCompanyId}
and pub_driver.platformtype=1
AND( pub_driver.name like "%"#{queryKeyword}"%" OR pub_driver.phone like "%"#{queryKeyword}"%")
AND pub_driver.workstatus=#{queryWorkStatus}
AND pub_driver.city like "%"#{queryCity}"%"
AND pub_driver.jobstatus like "%"#{queryJobStatus}"%"
AND pub_driver.jobNum = #{jobNum}
AND pub_driver.id = #{driverId}
AND pub_driver.identitytype like "%"#{queryIdEntityType}"%"
AND #{queryServiceOrg} in (SELECT org_organ.shortname FROM org_organ_driver_ref, org_organ WHERE org_organ_driver_ref.driverid = pub_driver.id AND org_organ.id = org_organ_driver_ref.organid)
AND pub_driver.boundstate=#{queryBoundState}
AND pub_driver.vehicletype=#{queryVehicleType}
AND pub_driver.id=#{queryJobNum}
ORDER BY jobstatus, CONVERT ((SELECT pub_cityaddr.city FROM pub_cityaddr WHERE pub_cityaddr.`status`=1 AND pub_cityaddr.id = pub_driver.city) USING gbk) COLLATE gbk_chinese_ci, pub_driver.vehicletype DESC, (CASE workstatus WHEN 2 THEN 1 WHEN 0 THEN 2 WHEN 1 THEN 3 ELSE 0 END) ) m ) t WHERE
t.indexnum > #{iDisplayStart} and t.indexnum <= (#{iDisplayStart} + #{iDisplayLength})

SELECT count(*) FROM ( SELECT (@rownum := @rownum + 1) AS indexnum, m.* FROM ( SELECT *, (SELECT pub_cityaddr.city FROM pub_cityaddr WHERE pub_cityaddr.`status`=1 AND pub_cityaddr.id = pub_driver.city) AS cityName, (SELECT text FROM pub_dictionary where pub_dictionary.Type='服务状态' and pub_dictionary.`Value`=pub_driver.workstatus and pub_dictionary.status=1)as workStatusName FROM pub_driver, (SELECT @rownum := 0) r WHERE pub_driver.`Status` = 1 AND pub_driver.leasescompanyid= #{leasesCompanyId}
and pub_driver.platformtype=1
AND( pub_driver.name like "%"#{queryKeyword}"%" OR pub_driver.phone like "%"#{queryKeyword}"%")
AND pub_driver.workstatus=#{queryWorkStatus}
AND pub_driver.city = #{queryCity}
AND pub_driver.jobstatus like "%"#{queryJobStatus}"%"
AND pub_driver.identitytype like "%"#{queryIdEntityType}"%"
AND #{queryServiceOrg} in (SELECT org_organ.shortname FROM org_organ_driver_ref, org_organ WHERE org_organ_driver_ref.driverid = pub_driver.id AND org_organ.id = org_organ_driver_ref.organid)
AND pub_driver.boundstate=#{queryBoundState}
AND pub_driver.vehicletype=#{queryVehicleType}
AND pub_driver.id=#{queryJobNum}
) m ) t

SELECT t.* FROM ( SELECT (@rownum := @rownum + 1) AS indexnum, m.* FROM ( SELECT org_organ.id, org_organ.fullname, org_organ.city, ( SELECT pub_cityaddr.city FROM pub_cityaddr WHERE pub_cityaddr.id = org_organ.city AND pub_cityaddr. STATUS = 1 ) AS citycaption, ( SELECT text FROM pub_dictionary WHERE pub_dictionary.Type = '结算方式' AND pub_dictionary.`Value` = org_organ.billtype ) AS billtype FROM org_organ, org_organ_company_ref, (SELECT @rownum := 0) r WHERE org_organ. STATUS = 1 AND org_organ_company_ref. STATUS = 1 AND org_organ_company_ref.organid = org_organ.id AND org_organ_company_ref.companyid = #{leasesCompanyId} ) m where 1=1
AND m.city = #{queryServiceOrgCity}
AND m.id = #{queryServiceOrgFullName}
ORDER BY CONVERT (m.citycaption USING gbk) COLLATE gbk_chinese_ci, CONVERT (m.fullname USING gbk) COLLATE gbk_chinese_ci ) t WHERE
t.indexnum > #{iDisplayStart} and t.indexnum <= (#{iDisplayStart} + #{iDisplayLength})

SELECT count(*) FROM ( SELECT (@rownum := @rownum + 1) AS indexnum, m.* FROM ( SELECT org_organ.id, org_organ.fullname, org_organ.city, ( SELECT pub_cityaddr.city FROM pub_cityaddr WHERE pub_cityaddr.id = org_organ.city AND pub_cityaddr. STATUS = 1 ) AS citycaption, ( SELECT text FROM pub_dictionary WHERE pub_dictionary.Type = '结算方式' AND pub_dictionary.`Value` = org_organ.billtype ) AS billtype FROM org_organ, org_organ_company_ref, (SELECT @rownum := 0) r WHERE org_organ. STATUS = 1 AND org_organ_company_ref. STATUS = 1 AND org_organ_company_ref.organid = org_organ.id AND org_organ_company_ref.companyid = #{leasesCompanyId} ) m where 1=1
AND m.city = #{queryServiceOrgCity}
AND m.id = #{queryServiceOrgFullName}
ORDER BY CONVERT (m.citycaption USING gbk) COLLATE gbk_chinese_ci, CONVERT (m.fullname USING gbk) COLLATE gbk_chinese_ci ) t

SELECT t.* FROM ( SELECT (@rownum := @rownum + 1) AS indexnum, m.* FROM ( SELECT *, (SELECT pub_cityaddr.city FROM pub_cityaddr WHERE pub_cityaddr.`status`=1 AND pub_cityaddr.id = pub_driver.city) AS cityName, ( SELECT pub_dictionary.text FROM pub_dictionary WHERE pub_dictionary.type = '租赁公司' AND pub_dictionary.`value` = pub_driver.belongleasecompany ) AS belongleasecompanyName, (SELECT text FROM pub_dictionary where pub_dictionary.Type='服务状态' and pub_dictionary.`Value`=pub_driver.workstatus and pub_dictionary.status=1)as workStatusName, (select text from pub_dictionary where pub_dictionary.type='驾驶类型' and pub_dictionary.`Value`=pub_driver.driverstype and pub_dictionary.status=1) as driversTypeName FROM pub_driver, (SELECT @rownum := 0) r WHERE pub_driver.`Status` = 1 AND pub_driver.leasescompanyid= #{leasesCompanyId}
and pub_driver.platformtype=1
AND( pub_driver.name like "%"#{queryKeyword}"%" OR pub_driver.phone like "%"#{queryKeyword}"%")
AND pub_driver.workstatus=#{queryWorkStatus}
AND pub_driver.city like "%"#{queryCity}"%"
AND pub_driver.jobstatus like "%"#{queryJobStatus}"%"
AND pub_driver.identitytype like "%"#{queryIdEntityType}"%"
AND #{queryServiceOrg} in (SELECT org_organ.shortname FROM org_organ_driver_ref, org_organ WHERE org_organ_driver_ref.driverid = pub_driver.id AND org_organ.id = org_organ_driver_ref.organid)
AND pub_driver.boundstate=#{queryBoundState}
AND pub_driver.vehicletype=#{queryVehicleType}
AND pub_driver.id=#{queryJobNum}
ORDER BY CONVERT ((SELECT pub_cityaddr.city FROM pub_cityaddr WHERE pub_cityaddr.`status`=1 AND pub_cityaddr.id = pub_driver.city) USING gbk) COLLATE gbk_chinese_ci, pub_driver.vehicletype DESC, workstatus DESC ) m ) t

SELECT t1.id AS id,t1.jobnum AS text FROM pub_driver t1 WHERE t1.leasescompanyid=#{leasesCompanyId}
AND t1.`Status`= 1 AND  t1.platformtype = 1

	and t1.jobnum like "%"#{queryJobNum}"%"
