mapper.select:
     select t.*,(@i:=@i+1) as code 
     from(
                 select  pub_premium_rule.id,
                         pub_premium_rule.isoperated,
                          pub_cityaddr.city as citycode,
                         IF(pub_premium_rule.cartype='0','网约车','出租车') as cartype ,
						 IF(STR_TO_DATE(pub_premium_rule.enddt,'%Y-%m-%d')<STR_TO_DATE(NOW(),'%Y-%m-%d'),"已过期",IF(pub_premium_rule.ruletype = '0','按星期','按日期')) as ruletype,
						 pub_premium_rule.rulename,
						 IF(pub_premium_rule.rulestatus='0','禁用','启用') as rulestatus,
						 (if (pub_premium_rule.ruletype = '0','永久', CONCAT(DATE_FORMAT(pub_premium_rule.startdt,'%Y-%m-%d'),'至',DATE_FORMAT(pub_premium_rule.enddt,'%Y-%m-%d')))) as isperpetual,
						 (select nickname from le_user where id = pub_premium_rule.updater) as updater
				from pub_premium_rule,pub_cityaddr 
				where pub_premium_rule.citycode = pub_cityaddr.id
				and pub_premium_rule.platformtype = 1
				AND pub_premium_rule.status = 1
				and pub_premium_rule.leasescompanyid = #{leasescompanyid}

	            and STR_TO_DATE(pub_premium_rule.startdt,'%Y-%m-%d')  >=  STR_TO_DATE(#{startdt},'%Y-%m-%d')


		        and STR_TO_DATE(pub_premium_rule.enddt,'%Y-%m-%d')  <=  STR_TO_DATE(#{enddt},'%Y-%m-%d')


	           and  pub_premium_rule.cartype = #{cartype}


		        and pub_premium_rule.ruletype = #{ruletype}


		       and pub_premium_rule.rulestatus = #{rulestatus}


		       and pub_premium_rule.rulename like "%"#{rulename}"%"


		       and pub_premium_rule.citycode = #{citycode}

	         ORDER BY pub_cityaddr.cityInitials,pub_premium_rule.cartype,pub_premium_rule.createtime DESC
           )t,(select @i:=0) a  LIMIT ${iDisplayStart},${iDisplayLength}

    select count(*) from(
                   select pub_premium_rule.id,
                         pub_cityaddr.city as citycode,
                         IF(pub_premium_rule.cartype='0','网约车','出租车') as cartype ,
						 IF(pub_premium_rule.ruletype = '0','按星期','按日期') as ruletype,
						 pub_premium_rule.rulename,
						 IF(pub_premium_rule.rulestatus='0','禁用','启用') as rulestatus,
						 if(pub_premium_rule.isperpetual='0',CONCAT(DATE_FORMAT(pub_premium_rule.startdt,'%Y/%m/%d'),'-',DATE_FORMAT(pub_premium_rule.enddt,'%Y/%m/%d')),'永久') as isperpetual,
						 (select nickname from le_user where id = pub_premium_rule.updater) as updater
					from pub_premium_rule,pub_cityaddr 
					where pub_premium_rule.citycode = pub_cityaddr.id
					and pub_premium_rule.platformtype = 1
					AND pub_premium_rule.status = 1
					and pub_premium_rule.leasescompanyid = #{leasescompanyid}

	            and STR_TO_DATE(pub_premium_rule.startdt,'%Y-%m-%d')  >=  STR_TO_DATE(#{startdt},'%Y-%m-%d')


			        and STR_TO_DATE(pub_premium_rule.enddt,'%Y-%m-%d')  <=  STR_TO_DATE(#{enddt},'%Y-%m-%d')


		           and  pub_premium_rule.cartype = #{cartype}


			        and pub_premium_rule.ruletype = #{ruletype}


			       and pub_premium_rule.rulestatus = #{rulestatus}


			       and pub_premium_rule.rulename = #{rulename}


			       and pub_premium_rule.citycode = #{citycode}

           )t

    SELECT * from (
       (SELECT *
				FROM
					pub_premium_rule
				WHERE
					citycode = #{citycode}
				AND cartype = #{cartype}
				AND id != #{id}
				AND status = 1
				and ruletype = #{ruletype}
				and rulestatus = 1
				and leasescompanyid = #{leasescompanyid}
				and platformtype = 1
				AND STR_TO_DATE(startdt,'%Y-%m-%d')  <=  STR_TO_DATE(#{startdt},'%Y-%m-%d')
				AND STR_TO_DATE(enddt,'%Y-%m-%d') >=  STR_TO_DATE(#{startdt},'%Y-%m-%d'))
      UNION
				(SELECT
					*
				FROM
					pub_premium_rule
				WHERE
					citycode = #{citycode}
				AND cartype = #{cartype}
				AND id != #{id}
				AND status = 1
				and ruletype = #{ruletype}
				and rulestatus = 1
				and leasescompanyid = #{leasescompanyid}
				and platformtype = 1
				AND STR_TO_DATE(startdt,'%Y-%m-%d') <=  STR_TO_DATE(#{enddt},'%Y-%m-%d')
				AND STR_TO_DATE(enddt,'%Y-%m-%d')  >= STR_TO_DATE(#{enddt},'%Y-%m-%d'))
     UNION
       (SELECT *
				FROM
					pub_premium_rule
				WHERE
					citycode = #{citycode}
				AND cartype = #{cartype}
				AND id != #{id}
				AND status = 1
				and ruletype = #{ruletype}
				and rulestatus = 1
				and leasescompanyid = #{leasescompanyid}
				and platformtype = 1
				AND STR_TO_DATE(startdt,'%Y-%m-%d')  >= STR_TO_DATE(#{startdt},'%Y-%m-%d')
				AND STR_TO_DATE(startdt,'%Y-%m-%d') <=  STR_TO_DATE(#{enddt},'%Y-%m-%d'))
     UNION
       (SELECT *
				FROM
					pub_premium_rule
				WHERE
					citycode = #{citycode}
				AND cartype = #{cartype}
				AND id != #{id}
				AND status = 1
				and ruletype = #{ruletype}
				and rulestatus = 1
				and leasescompanyid = #{leasescompanyid}
				and platformtype = 1
				AND STR_TO_DATE(enddt,'%Y-%m-%d')  >=  STR_TO_DATE(#{startdt},'%Y-%m-%d')
				AND STR_TO_DATE(enddt,'%Y-%m-%d')  <=  STR_TO_DATE(#{enddt},'%Y-%m-%d'))
)t

     select t.*,(@i:=@i+1) as code 
     from(
          SELECT
	id,premiumruleid,(CASE when weekday = 1 then "星期一" when weekday = 2 then "星期二" when weekday = 3 then "星期三" when weekday = 4 then "星期四" when weekday = 5 then "星期五" when weekday = 6 then "星期六" else  "星期日" end) as weekday,
   CONCAT(startdt,'-',enddt) as theTime,premiumrate,DATE_FORMAT(updatetime,'%Y/%m/%d %H:%i:%s') updatetime
FROM
	pub_premium_rule_weekdetail
WHERE
	premiumruleid = #{id}

	   and pub_premium_rule_weekdetail.weekday = #{weekday}

ORDER BY
	pub_premium_rule_weekdetail.weekday,
	startdt 
           )t,(select @i:=0) a  LIMIT ${iDisplayStart},${iDisplayLength}

    select count(*) from(
                   select * from pub_premium_rule_weekdetail where premiumruleid = #{id} 

	               and pub_premium_rule_weekdetail.weekday = #{weekday}

                   ORDER BY weekday,startdt
           )t

select t.*,(@i:=@i+1) as code from( SELECT id,premiumruleid, CONCAT(starttime,'-',endtime) as theTime,premiumrate,DATE_FORMAT(updatetime,'%Y/%m/%d %H:%i:%s') updatetime, CONCAT(DATE_FORMAT(startdate,'%Y/%m/%d'),'-',DATE_FORMAT(enddate,'%Y/%m/%d'))as weekday FROM pub_premium_rule_datedetail WHERE premiumruleid = #{id}
and STR_TO_DATE(pub_premium_rule_datedetail.startdate,'%Y-%m-%d') >= STR_TO_DATE(#{startTime},'%Y-%m-%d')
and STR_TO_DATE(pub_premium_rule_datedetail.enddate,'%Y-%m-%d') <= STR_TO_DATE(#{endTime},'%Y-%m-%d')
ORDER BY starttime )t,(select @i:=0) a LIMIT ${iDisplayStart},${iDisplayLength}

     select t.*,(@i:=@i+1) as code 
     from(
          SELECT
	id,operationid,(CASE when weekday = 1 then "星期一" when weekday = 2 then "星期二" when weekday = 3 then "星期三" when weekday = 4 then "星期四" when weekday = 5 then "星期五" when weekday = 6 then "星期六" else  "星期日" end) as weekday,
   CONCAT(startdt,'-',enddt) as theTime,premiumrate,DATE_FORMAT(updatetime,'%Y/%m/%d %H:%i') updatetime
FROM
	pub_premium_rule_weekdetail_history
WHERE
	operationid = #{id}
ORDER BY
	pub_premium_rule_weekdetail_history.weekday,
	startdt 
           )t,(select @i:=0) a  LIMIT ${iDisplayStart},${iDisplayLength}

select count(*) from(
               select * from pub_premium_rule_weekdetail_history where operationid = #{id} ORDER BY weekday,startdt
       )t

     select t.*,(@i:=@i+1) as code 
     from(
         SELECT
      id,operationid, CONCAT(starttime,'-',endtime) as theTime,premiumrate,DATE_FORMAT(updatetime,'%Y/%m/%d %H:%i') updatetime,
			CONCAT(DATE_FORMAT(startdate,'%Y/%m/%d'),'-',DATE_FORMAT(enddate,'%Y/%m/%d'))as weekday
		FROM
			pub_premium_rule_datedetail_history
        WHERE
	        operationid = #{id}
        ORDER BY
	   starttime 
           )t,(select @i:=0) a  LIMIT ${iDisplayStart},${iDisplayLength}
