mapper.select:
SELECT COUNT(*) FROM pub_vehicle WHERE 1 = 1
AND (( platenoprovince = #{plateNoProvince} AND platenocity = #{plateNoCity} AND plateno = #{plateNo} ) OR vin = #{vin})
AND `Status` = 1
AND id != #{id}
AND CONCAT( ( SELECT pub_dictionary.text FROM pub_dictionary WHERE pub_dictionary.type = '车牌省' AND pub_dictionary.`value` = pub_vehicle.platenoprovince AND pub_dictionary.`status` = 1 ), ( SELECT pub_dictionary.text FROM pub_dictionary WHERE pub_dictionary.type = '车牌市' AND pub_dictionary.`value` = pub_vehicle.platenocity AND pub_dictionary.`status` = 1 ), pub_vehicle.plateno ) = #{showPlateNo}
and leasescompanyid=#{leasesCompanyId}
and pub_vehicle.platformtype = 1

SELECT COUNT(*) FROM pub_vehicle, pub_driver_vehicle_ref WHERE pub_driver_vehicle_ref.vehicleid = pub_vehicle.id AND pub_vehicle.`Status` = 1 AND pub_driver_vehicle_ref.`Status` = 1
AND pub_vehicle.id=#{id}
and pub_vehicle.platformtype = 1

SELECT pub_vehicle.leasescompanyid, pub_vehicle.id, pub_vehicle.vehclineid, CONCAT( pub_vehcbrand.`Name`, " ", pub_vehcline.`name` ) AS brandCars, CONCAT( ( SELECT pub_dictionary.text FROM pub_dictionary WHERE pub_dictionary.type = '车牌省' AND pub_dictionary.`value` = pub_vehicle.platenoprovince AND pub_dictionary.`status` = 1 ), ( SELECT pub_dictionary.text FROM pub_dictionary WHERE pub_dictionary.type = '车牌市' AND pub_dictionary.`value` = pub_vehicle.platenocity AND pub_dictionary.`status` = 1 ), pub_vehicle.plateno ) AS showPlateNo, le_vehiclemodels.`name` AS serviceCars, pub_vehicle.vin, pub_vehicle.color, ( SELECT pub_cityaddr.city FROM pub_cityaddr WHERE pub_cityaddr.`status` = 1 AND pub_cityaddr.id = pub_vehicle.city ) AS city, (SELECT t1.identitytype FROM pub_driver t1, pub_driver_vehicle_ref t2 WHERE t1.`Status` = 1 AND t1.platformtype = 1 AND t2.`status` = 1 AND t2.driverid = t1.id AND t2.vehicleid = pub_vehicle.id AND ((t1.vehicletype = '1' AND t1.id = pub_vehicle.driverid AND t1.passworkstatus IN( '0' ,'1' ,'3')) OR (t1.vehicletype = '0'))
) AS identitytype, (SELECT ( CASE WHEN pub_vehicle.boundstate = 0 THEN '3' ELSE t1.workstatus END ) workstatus FROM pub_driver t1, pub_driver_vehicle_ref t2 WHERE t1.`Status` = 1 AND t1.platformtype = 1 AND t2.`status` = 1 AND t2.driverid = t1.id AND t2.vehicleid = pub_vehicle.id AND ((t1.vehicletype = '1' AND t1.id = pub_vehicle.driverid AND t1.passworkstatus IN( '0' ,'1' ,'3')) OR (t1.vehicletype = '0'))
) AS workStatus1, (SELECT ( CASE t1.workstatus WHEN 2 THEN 1 WHEN 0 THEN 2 WHEN 1 THEN 3 ELSE 0 END ) workstatus FROM pub_driver t1, pub_driver_vehicle_ref t2 WHERE t1.`Status` = 1 AND t1.platformtype = 1 AND t2.`status` = 1 AND t2.driverid = t1.id AND t2.vehicleid = pub_vehicle.id AND ((t1.vehicletype = '1' AND t1.id = pub_vehicle.driverid AND t1.passworkstatus IN( '0' ,'1' ,'3')) OR (t1.vehicletype = '0'))
), ( SELECT text FROM pub_dictionary WHERE pub_dictionary.Type = '服务状态' AND pub_dictionary.`Value` = workStatus1 AND pub_dictionary.`status` = 1 ) AS workStatus, ( SELECT pub_dictionary.text FROM pub_dictionary WHERE pub_dictionary.type = '租赁公司' AND pub_dictionary.`value` = pub_vehicle.belongleasecompany ) AS belongleasecompanyName, pub_vehicle.boundstate, pub_vehicle.loads, pub_vehicle.vehicletype, pub_vehicle.vehiclestatus FROM pub_vehicle LEFT JOIN pub_vehcline ON pub_vehicle.vehclineid = pub_vehcline.Id LEFT JOIN pub_vehcbrand ON pub_vehcline.vehcBrandID = pub_vehcbrand.Id LEFT JOIN le_vehcline_models_ref ON pub_vehicle.vehclineid = le_vehcline_models_ref.vehclineid LEFT JOIN le_vehiclemodels ON le_vehiclemodels.Id = le_vehcline_models_ref.vehiclemodelsid WHERE pub_vehcline.`Status` = 1 AND pub_vehcbrand.`Status` = 1 AND pub_vehicle.`Status` = 1 AND pub_vehicle.platformtype = 1 AND pub_vehcline.platformtype = 1 AND pub_vehcbrand.platformtype = 1 AND pub_vehicle.leasescompanyid=#{leasesCompanyId}
AND CONCAT( ( SELECT pub_dictionary.text FROM pub_dictionary WHERE pub_dictionary.type = '车牌省' AND pub_dictionary.`value` = pub_vehicle.platenoprovince AND pub_dictionary.`status` = 1 ), ( SELECT pub_dictionary.text FROM pub_dictionary WHERE pub_dictionary.type = '车牌市' AND pub_dictionary.`value` = pub_vehicle.platenocity AND pub_dictionary.`status` = 1 ), pub_vehicle.plateno ) like "%"#{queryPlateNo}"%"
AND le_vehiclemodels.`name` like "%"#{queryServiceCars}"%" and pub_vehicle.vehicletype = '0'
AND (SELECT pub_cityaddr.city FROM pub_cityaddr WHERE pub_cityaddr.`status`=1 AND pub_cityaddr.id = pub_vehicle.city)=#{queryCity}
AND ( SELECT ( CASE WHEN pub_vehicle.boundstate = 0 THEN '3' ELSE t1.workstatus END ) workstatus FROM pub_driver t1, pub_driver_vehicle_ref t2 WHERE t1.`Status` = 1 AND t1.platformtype = 1 AND t2.`status` = 1 AND t2.driverid = t1.id AND t2.vehicleid = pub_vehicle.id AND ((t1.vehicletype = '1' AND t1.id = pub_vehicle.driverid AND t1.passworkstatus IN( '0' ,'1' ,'3')) OR (t1.vehicletype = '0'))
) like "%"#{queryWorkStatus}"%"
AND pub_vehicle.vehclineid = #{queryBrandCars}
AND pub_vehicle.vehicletype = #{queryVehicleType}
AND pub_vehicle.vehiclestatus = #{queryVehicleStatus}
and pub_vehicle.boundstate = #{queryBoundState}
AND pub_vehicle.belongleasecompany=#{belongleasecompanyQuery}
ORDER BY CONVERT ( ( SELECT pub_cityaddr.city FROM pub_cityaddr WHERE pub_cityaddr.`status` = 1 AND pub_cityaddr.id = pub_vehicle.city ) USING gbk ) COLLATE gbk_chinese_ci, pub_vehicle.vehicletype DESC, le_vehiclemodels.level, CONVERT((le_vehiclemodels.`name`)USING gbk)COLLATE gbk_chinese_ci, (SELECT ( CASE t1.workstatus WHEN 2 THEN 1 WHEN 0 THEN 2 WHEN 1 THEN 3 ELSE 0 END ) workstatus FROM pub_driver t1, pub_driver_vehicle_ref t2 WHERE t1.`Status` = 1 AND t1.platformtype = 1 AND t2.`status` = 1 AND t2.driverid = t1.id AND t2.vehicleid = pub_vehicle.id AND ((t1.vehicletype = '1' AND t1.id = pub_vehicle.driverid AND t1.passworkstatus IN( '0' ,'1' ,'3')) OR (t1.vehicletype = '0'))
) LIMIT #{iDisplayStart} ,#{iDisplayLength}

SELECT count(*) FROM pub_vehicle LEFT JOIN pub_vehcline ON pub_vehicle.vehclineid = pub_vehcline.Id LEFT JOIN pub_vehcbrand ON pub_vehcline.vehcBrandID = pub_vehcbrand.Id LEFT JOIN le_vehcline_models_ref ON pub_vehicle.vehclineid = le_vehcline_models_ref.vehclineid LEFT JOIN le_vehiclemodels ON le_vehiclemodels.Id = le_vehcline_models_ref.vehiclemodelsid WHERE pub_vehcline.`Status` = 1 AND pub_vehcbrand.`Status` = 1 AND pub_vehicle.`Status` = 1 AND pub_vehicle.platformtype = 1 AND pub_vehcline.platformtype = 1 AND pub_vehcbrand.platformtype = 1 AND pub_vehicle.leasescompanyid=#{leasesCompanyId}
AND CONCAT( ( SELECT pub_dictionary.text FROM pub_dictionary WHERE pub_dictionary.type = '车牌省' AND pub_dictionary.`value` = pub_vehicle.platenoprovince AND pub_dictionary.`status` = 1 ), ( SELECT pub_dictionary.text FROM pub_dictionary WHERE pub_dictionary.type = '车牌市' AND pub_dictionary.`value` = pub_vehicle.platenocity AND pub_dictionary.`status` = 1 ), pub_vehicle.plateno ) like "%"#{queryPlateNo}"%"
AND le_vehiclemodels.`name` like "%"#{queryServiceCars}"%" and pub_vehicle.vehicletype = '0'
AND (SELECT pub_cityaddr.city FROM pub_cityaddr WHERE pub_cityaddr.`status`=1 AND pub_cityaddr.id = pub_vehicle.city)=#{queryCity}
AND ( SELECT ( CASE WHEN pub_vehicle.boundstate = 0 THEN '3' ELSE t1.workstatus END ) workstatus FROM pub_driver t1, pub_driver_vehicle_ref t2 WHERE t1.`Status` = 1 AND t1.platformtype = 1 AND t2.`status` = 1 AND t2.driverid = t1.id AND t2.vehicleid = pub_vehicle.id AND ((t1.vehicletype = '1' AND t1.id = pub_vehicle.driverid AND t1.passworkstatus IN( '0' ,'1' ,'3')) OR (t1.vehicletype = '0'))
) like "%"#{queryWorkStatus}"%"
AND pub_vehicle.vehclineid = #{queryBrandCars}
AND pub_vehicle.vehicletype = #{queryVehicleType}
AND pub_vehicle.vehiclestatus = #{queryVehicleStatus}
and pub_vehicle.boundstate = #{queryBoundState}
AND pub_vehicle.belongleasecompany=#{belongleasecompanyQuery}

