) AS workStatus1, ( SELECT text FROM pub_dictionary WHERE pub_dictionary.Type = '服务状态' AND pub_dictionary.`Value` = workStatus1 AND pub_dictionary.`status` = 1 ) AS workStatus, ( SELECT pub_dictionary.text FROM pub_dictionary WHERE pub_dictionary.type = '租赁公司' AND pub_dictionary.`value` = pub_vehicle.belongleasecompany ) AS belongleasecompanyName, pub_vehicle.boundstate, pub_vehicle.loads, pub_vehicle.vehicletype, pub_vehicle.vehiclestatus FROM pub_vehicle LEFT JOIN pub_vehcline ON pub_vehicle.vehclineid = pub_vehcline.Id LEFT JOIN pub_vehcbrand ON pub_vehcline.vehcBrandID = pub_vehcbrand.Id LEFT JOIN le_vehcline_models_ref ON pub_vehicle.vehclineid = le_vehcline_models_ref.vehclineid LEFT JOIN le_vehiclemodels ON le_vehiclemodels.Id = le_vehcline_models_ref.vehiclemodelsid WHERE pub_vehcline.`Status` = 1 AND pub_vehcbrand.`Status` = 1 AND pub_vehicle.`Status` = 1 AND pub_vehicle.platformtype = 1 AND pub_vehcline.platformtype = 1 AND pub_vehcbrand.platformtype = 1 AND pub_vehicle.leasescompanyid=#{leasesCompanyId}
AND CONCAT( ( SELECT pub_dictionary.text FROM pub_dictionary WHERE pub_dictionary.type = '车牌省' AND pub_dictionary.`value` = pub_vehicle.platenoprovince AND pub_dictionary.`status` = 1 ), ( SELECT pub_dictionary.text FROM pub_dictionary WHERE pub_dictionary.type = '车牌市' AND pub_dictionary.`value` = pub_vehicle.platenocity AND pub_dictionary.`status` = 1 ), pub_vehicle.plateno ) like "%"#{queryPlateNo}"%"
