AND ( SELECT ( CASE WHEN pub_vehicle.boundstate = 0 THEN '3' ELSE t1.workstatus END ) workstatus FROM pub_driver t1, pub_driver_vehicle_ref t2 WHERE t1.`Status` = 1 AND t1.platformtype = 1 AND t2.`status` = 1 AND t2.driverid = t1.id AND t2.vehicleid = pub_vehicle.id AND ((t1.vehicletype = '1' AND t1.id = pub_vehicle.driverid AND t1.passworkstatus IN( '0' ,'1' ,'3')) OR (t1.vehicletype = '0'))
) like "%"#{queryWorkStatus}"%"
AND pub_vehicle.vehclineid = #{queryBrandCars}
AND pub_vehicle.vehicletype = #{queryVehicleType}
AND pub_vehicle.vehiclestatus = #{queryVehicleStatus}
and pub_vehicle.boundstate = #{queryBoundState}
AND pub_vehicle.belongleasecompany=#{belongleasecompanyQuery}
ORDER BY CONVERT ( ( SELECT pub_cityaddr.city FROM pub_cityaddr WHERE pub_cityaddr.`status` = 1 AND pub_cityaddr.id = pub_vehicle.city ) USING gbk ) COLLATE gbk_chinese_ci, pub_vehicle.vehicletype DESC, CONVERT((le_vehiclemodels.`name`)USING gbk)COLLATE gbk_chinese_ci, ( SELECT ( CASE WHEN pub_vehicle.boundstate = 0 THEN '3' ELSE t1.workstatus END ) workstatus FROM pub_driver t1, pub_driver_vehicle_ref t2 WHERE t1.`Status` = 1 AND t1.platformtype = 1 AND t2.`status` = 1 AND t2.driverid = t1.id AND t2.vehicleid = pub_vehicle.id AND ((t1.vehicletype = '1' AND t1.id = pub_vehicle.driverid AND t1.passworkstatus IN( '0' ,'1' ,'3')) OR (t1.vehicletype = '0'))
) DESC

choose.when:
/**下线状态需判断 车辆状态为维修且当班司机为空；或当班司机状态为下线*/
and ((t1.vehiclestatus ='1' and t1.driverid ='')
or t2.workstatus = #{driverState})

mapper.select:
select count(t1.id) from pub_vehicle t1
left join pub_driver t2 on t1.driverid = t2.id
where t1.platformtype = #{platformtype}
and t1.leasescompanyid = #{leasesCompanyId}
