mapper.select:
select t.* from (select (@rownum := @rownum + 1) as rownum,t1.* from (select org_order.ordersource, org_order.orderno, org_order.usetype, org_order.ordertype, org_order.orderstatus, org_order.paymentstatus, org_order.paytype, org_order.orderamount, org_order.mileage, org_order.shouldpayamount, org_order.starttime, org_order.endtime, org_order.pricecopy, org_order.passengers, org_order.passengerphone, date_format(org_order.usetime, '%Y/%m/%d %H:%i') usetime, org_order.cancelparty, org_orderpaymentrecord.tradeno, (select concat(org_user.nickname,' ',org_user.account) from org_user where org_user.id = org_order.userid) as orderperson, (select concat(pub_driver.name,' ',pub_driver.phone) from pub_driver where pub_driver.id = org_order.driverid) as driver, (select pub_cityaddr.city from pub_cityaddr where pub_cityaddr.id = org_order.oncity) as oncity, (select pub_cityaddr.city from pub_cityaddr where pub_cityaddr.id = org_order.offcity) as offcity, org_order.onaddress, org_order.offaddress, orderreview2.times, orderreview2.counttimes, orderreview2.mileage as orderreviewmileage, orderreview2.orderno as revieworderno , ifnull(le_leasescompany.shortname, '/') shortname from org_order left join org_orderpaymentrecord on (org_order.orderno = org_orderpaymentrecord.orderno and org_orderpaymentrecord.status = 0 and org_orderpaymentrecord.tradeno is not null) left join ( select orderreview1.* from (select org_orderreview.orderno,org_orderreview.times,org_orderreview.counttimes,org_orderreview.mileage,org_orderreview.createtime from org_orderreview where org_orderreview.status = 1 order by org_orderreview.createtime desc) orderreview1 group by orderreview1.orderno ) orderreview2 on (org_order.orderno = orderreview2.orderno) left join le_leasescompany on le_leasescompany.id = org_order.companyid where org_order.belongleasecompany = #{belongleasecompany}
and org_order.companyid <> #{belongleasecompany}
and org_order.status = 1
and org_order.orderno like concat('%',#{orderNo},'%')
and org_order.companyid = #{companyId}
and org_order.ordertype = #{orderType}
and org_order.orderstatus = #{orderStatus}
and org_order.orderstatus = '7' and org_order.paymentstatus = '0'
and org_order.orderstatus = '7' and org_order.paymentstatus = '1'
and org_order.orderstatus in ('2','3','4','6','7')
and org_order.userid like concat('%',#{orderPerson},'%')
and org_order.driverid like concat('%',#{driver},'%')
and org_order.paytype = #{payType}
and org_order.cancelparty = #{cancelParty}
and org_order.ordersource ='20'
and org_order.ordersource in ('01','00') and org_order.usetype='0'
and org_order.ordersource in ('01','00') and org_order.usetype='1'
and org_order.ordersource ='10' and org_order.usetype='0'
and org_order.ordersource ='10' and org_order.usetype='1'
and org_orderpaymentrecord.tradeno like concat('%',#{tradeNo},'%')
and org_order.usetime >= #{minUseTime}
and org_order.usetime <= #{maxUseTime}
order by org_order.orderstatus,org_order.paymentstatus, case when org_order.orderstatus = '0' or org_order.orderstatus = '1' or org_order.orderstatus = '2' then org_order.usetime else '' end asc, case when org_order.orderstatus != '0' and org_order.orderstatus != '1' and org_order.orderstatus != '2' then org_order.usetime else '' end desc ) t1, (select @rownum := 0) r ) t
where t.rownum > #{iDisplayStart} and t.rownum <= (#{iDisplayStart} + #{iDisplayLength})

select count(*) from org_order left join org_orderpaymentrecord on (org_order.orderno = org_orderpaymentrecord.orderno and org_orderpaymentrecord.status = 0 and org_orderpaymentrecord.tradeno is not null) left join ( select orderreview1.* from (select org_orderreview.orderno,org_orderreview.times,org_orderreview.counttimes,org_orderreview.mileage,org_orderreview.createtime from org_orderreview where org_orderreview.status = 1 order by org_orderreview.createtime desc) orderreview1 group by orderreview1.orderno ) orderreview2 on (org_order.orderno = orderreview2.orderno) where org_order.belongleasecompany = #{belongleasecompany}
and org_order.companyid <> #{belongleasecompany}
and org_order.status = 1
and org_order.orderno like concat('%',#{orderNo},'%')
and org_order.companyid = #{companyId}
and org_order.ordertype = #{orderType}
and org_order.orderstatus = #{orderStatus}
and org_order.orderstatus = '7' and org_order.paymentstatus = '0'
and org_order.orderstatus = '7' and org_order.paymentstatus = '1'
and org_order.orderstatus in ('2','3','4','6','7')
and org_order.userid like concat('%',#{orderPerson},'%')
and org_order.driverid like concat('%',#{driver},'%')
and org_order.paytype = #{payType}
and org_order.cancelparty = #{cancelParty}
and org_order.ordersource ='20'
and org_order.ordersource in ('01','00') and org_order.usetype='0'
and org_order.ordersource in ('01','00') and org_order.usetype='1'
and org_order.ordersource ='10' and org_order.usetype='0'
and org_order.ordersource ='10' and org_order.usetype='1'
and org_orderpaymentrecord.tradeno like concat('%',#{tradeNo},'%')
and org_order.usetime >= #{minUseTime}
and org_order.usetime <= #{maxUseTime}

select org_order.ordersource,org_order.orderno,org_order.ordertype,org_order.orderstatus,org_order.paymentstatus,org_order.paytype,org_order.orderamount,org_order.mileage, org_order.starttime,org_order.endtime,org_order.pricecopy,org_order.passengers,org_order.passengerphone, date_format(org_order.usetime, '%Y/%m/%d %H:%i') usetime,org_order.cancelparty,org_orderpaymentrecord.tradeno, (select concat(org_user.nickname,' ',org_user.account) from org_user where org_user.id = org_order.userid) as orderperson, concat(pub_driver.name,' ',pub_driver.phone) as driver,pub_driver.jobnum, (select pub_cityaddr.city from pub_cityaddr where pub_cityaddr.id = org_order.oncity) as oncity, (select pub_cityaddr.city from pub_cityaddr where pub_cityaddr.id = org_order.offcity) as offcity, org_order.onaddress,org_order.offaddress,org_order.estimatedtime,org_order.estimatedmileage, org_order.selectedmodelname,org_order.shouldpayamount,org_order.plateno,le_leasescompany.shortname as leasescompanyname, date_format(org_order.undertime, '%Y/%m/%d %H:%i') undertime, date_format(org_order.ordertime, '%Y/%m/%d %H:%i') ordertime, date_format(org_order.starttime, '%Y/%m/%d %H:%i') starttimeexport, date_format(org_order.endtime, '%Y/%m/%d %H:%i') endtimeexport, orderreview2.times,orderreview2.counttimes,orderreview2.mileage as orderreviewmileage,orderreview2.orderno as revieworderno, ifnull(le_leasescompany.shortname, '/') shortname from org_order left join org_orderpaymentrecord on (org_order.orderno = org_orderpaymentrecord.orderno and org_orderpaymentrecord.status = 0 and org_orderpaymentrecord.tradeno is not null) left join pub_driver on (pub_driver.id = org_order.driverid) left join le_leasescompany on (le_leasescompany.id = org_order.companyid and le_leasescompany.status = 1) left join ( select orderreview1.* from (select org_orderreview.orderno,org_orderreview.times,org_orderreview.counttimes,org_orderreview.mileage,org_orderreview.createtime from org_orderreview where org_orderreview.status = 1 order by org_orderreview.createtime desc) orderreview1 group by orderreview1.orderno ) orderreview2 on (org_order.orderno = orderreview2.orderno) where org_order.belongleasecompany = #{belongleasecompany}
and org_order.companyid <> #{belongleasecompany}
and org_order.status = 1
and org_order.orderno like concat('%',#{orderNo},'%')
and org_order.ordertype = #{orderType}
and org_order.orderstatus = #{orderStatus}
and org_order.orderstatus = '7' and org_order.paymentstatus = '0'
and org_order.orderstatus = '7' and org_order.paymentstatus = '1'
and org_order.orderstatus in ('2','3','4','6','7')
and org_order.userid like concat('%',#{orderPerson},'%')
and org_order.driverid like concat('%',#{driver},'%')
and org_order.paytype = #{payType}
and org_order.cancelparty = #{cancelParty}
and org_order.ordersource ='20'
and org_order.ordersource in ('01','00') and org_order.usetype='0'
and org_order.ordersource in ('01','00') and org_order.usetype='1'
and org_order.ordersource ='10' and org_order.usetype='0'
and org_order.ordersource ='10' and org_order.usetype='1'
and org_orderpaymentrecord.tradeno like concat('%',#{tradeNo},'%')
and org_order.usetime >= #{minUseTime}
and org_order.usetime <= #{maxUseTime}
order by org_order.orderstatus,org_order.paymentstatus, case when org_order.orderstatus = '0' or org_order.orderstatus = '1' or org_order.orderstatus = '2' then org_order.usetime else '' end asc, case when org_order.orderstatus != '0' and org_order.orderstatus != '1' and org_order.orderstatus != '2' then org_order.usetime else '' end desc
