mapper.select:
SELECT t.* FROM ( SELECT (@rownum := @rownum + 1) AS rownum, le_user.*, le_roleuser.roleid, le_rolemanagement.rolename FROM le_user left join le_roleuser on le_user.id = le_roleuser.userid left join le_rolemanagement on le_roleuser.roleid = le_rolemanagement.id, (SELECT @rownum := 0) r WHERE le_user.status = 1 AND le_user.leasescompanyid = #{leasescompanyid} AND le_user.specialstate != 1
and le_user.id = #{accountid}
and le_user.id = #{nicknameid}
and le_roleuser.roleid = #{roleid}
order by CONVERT(rolename USING gbk ),updatetime DESC ) t
where t.rownum > #{iDisplayStart} and t.rownum <= (#{iDisplayStart} + #{iDisplayLength})

select count(*) from le_user le_user left join le_roleuser on le_user.id = le_roleuser.userid
where le_user.status = 1
		AND le_user.leasescompanyid = #{leasescompanyid}
		AND le_user.specialstate != 1

			and le_user.id = #{accountid}


			and le_user.id = #{nicknameid}


			and le_roleuser.roleid = #{roleid}
