mapper.select:
select t.* from (select (@rownum := @rownum + 1) as rownum,t1.* from (select pca.id, pca.name, pca.sendservicetype, pca.sendruletype, pca.activystate, pca.sendmoneytype, pca.sendfixedmoney, pca.sendlowmoney, pca.sendhighmoney, pca.sendcount, pca.usetype, pca.outimetype, pca.sendtimeinday, pcr.name as rulename, date_format(pca.createtime,'%Y-%m-%d %H:%i') as createtime, date_format(pca.sendstarttime,'%Y-%m-%d') as sendstarttime, date_format(pca.sendendtime,'%Y-%m-%d') as sendendtime, date_format(pca.fixedstarttime,'%Y-%m-%d') as fixedstarttime, date_format(pca.fixedendtime,'%Y-%m-%d') as fixedendtime, group_concat(pc.city SEPARATOR '、') as citys from pub_coupon_activity pca left join pub_coupon_rule pcr on pca.sendruleidref=pcr.id left join pub_coupon_activity_use_city pcauc on pca.id=pcauc.couponactivityidref left join pub_cityaddr pc on pcauc.city=pc.id where pca.platformtype=0
and pca.sendservicetype = #{querysendservicetype}
and pca.sendruletype = #{querysendruletype}
and pcr.ruletype = #{queryruletype}
and pca.activystate = #{queryactivystate}
and pca.usetype = #{queryusetype}
and pca.id=#{queryname}
and pca.sendruleidref=#{querysendruleidref}
and date_format(pca.sendstarttime,'%Y-%m-%d') >= #{querysendstarttime}
and date_format(pca.sendendtime,'%Y-%m-%d') <= #{querysendendtime}
and pca.status='1'
group by pca.id order by pca.createtime desc ) t1, (select @rownum := 0) r )t
where t.rownum > #{iDisplayStart} and t.rownum <= (#{iDisplayStart} + #{iDisplayLength})

           select count(*) from pub_coupon_activity pca 
              left join pub_coupon_rule pcr on pca.sendruleidref=pcr.id 
              where pca.platformtype=0

	            and pca.sendservicetype = #{querysendservicetype}


	            and pca.sendruletype = #{querysendruletype}


	            and pcr.ruletype = #{queryruletype}


	            and pca.activystate = #{queryactivystate}


	            and pca.usetype = #{queryusetype}


	            and pca.id=#{queryname}


	            and pca.sendruleidref=#{querysendruleidref}


	            and pca.sendstarttime    >=   #{querysendstarttime}


	            and pca.sendendtime    <=   #{querysendendtime}

                and pca.status='1'

mapper.update:
update pub_coupon_activity
    set activystate=#{activystate}
    where id=#{id} and activystate=2 and platformtype=0 and status='1'

mapper.select:
           select t.* from
           (select (@rownum := @rownum + 1) as rownum,t1.* from
           (select pu.account,pc.money,
            date_format(pc.createtime,'%Y-%m-%d %H:%i') as createtime,
            date_format(pc.outimestart,'%Y-%m-%d') as outimestart,
            date_format(pc.outtimeend,'%Y-%m-%d') as outtimeend,
            pc.couponstatus 
            from pub_coupon pc
          inner join pe_user pu on pc.userid=pu.id
          where pc.couponactivyidref=#{id}
            and pc.platformtype=0
            and pc.status='1'
            and pu.status='1'

	            and pu.account=#{account}


	            and pc.couponstatus=#{couponstatus}


	            and date_format(pc.createtime,'%Y-%m-%d')    >=   #{sendstarttime}


	            and date_format(pc.createtime,'%Y-%m-%d')    <=   #{sendendtime}

	           order by pc.createtime desc
	           ) t1, (select @rownum := 0) r )t

	            where t.rownum > #{iDisplayStart} and t.rownum <=  (#{iDisplayStart} +  #{iDisplayLength})

          select count(*) from pub_coupon pc
          inner join pe_user pu on pc.userid=pu.id
          where pc.couponactivyidref=#{id}
            and pc.platformtype=0
            and pc.status='1'
            and pu.status='1'

	            and pu.account=#{account}


	            and pc.couponstatus=#{couponstatus}


	            and date_format(pc.createtime,'%Y-%m-%d')    >=   #{sendstarttime}


	            and date_format(pc.createtime,'%Y-%m-%d')    <=   #{sendendtime}
