mapper.select:
select t.* from(select orderno,onaddress,offaddress,estimatedtime,ROUND(estimatedmileage,1) estimatedmileage,DATE_FORMAT(undertime,'%Y-%m-%d %H:%i:%s')undertime,DATE_FORMAT(usetime,'%Y-%m-%d %H:%i:%s')usetime,DATE_FORMAT(ordertime,'%Y-%m-%d %H:%i:%s')ordertime,DATE_FORMAT(starttime,'%Y-%m-%d %H:%i:%s')starttime,DATE_FORMAT(endtime,'%Y-%m-%d %H:%i:%s')endtime, (case when ordertype = '1' then '约车' when ordertype ='2' then '接机' when ordertype = '3' then '送机' else '出租车'end) ordertype,organid, (case when ordersource in ('00','01','02') then '乘客端' when ordersource = '10' then '租赁端' when ordersource = '20' then '机构端' else '运管端' end) as ordersource, (case when paymentstatus = '0' then '未支付' when paymentstatus = '1' then '已支付' when paymentstatus = '2' then '结算中' when paymentstatus = '3' then '已结算' when paymentstatus = '4' then '未结算' when paymentstatus = '8' then '已付结' else '未付结' end)paymentstatus, ifnull(ROUND(mileage/1000,1),0.0) mileage,driverid,jobnum,name,phone,plateno,(select l.name from le_leasescompany l where l.id = companyid)companyid,passengerphone,passengers,userid,ifnull(ROUND(orderamount,1),0.0) orderamount, (case when orderstatus = '7' then '行程结束' else '已取消' end) orderstatus, ifnull(ROUND((UNIX_TIMESTAMP(endtime)-UNIX_TIMESTAMP(starttime))/60+0.49),0) runtime,cartype,(case when usetype = '0' then '机构因公' when usetype = '1' then '机构因私' else '个人' end) as usetype from(select orderno,onaddress,offaddress,estimatedtime,estimatedmileage,undertime,usetime,ordertime,starttime,endtime,ordertype,mileage,driverid,vehicleid, plateno,companyid,passengerphone,passengers,userid,paymentstatus,shouldpayamount orderamount,orderstatus ,departuretime,arrivaltime, pricemodelname cartype,organid, ordersource,usetype from org_order WHERE orderstatus in ('7','8') and status = '1' UNION ALL select orderno,onaddress,offaddress,estimatedtime,estimatedmileage,undertime,usetime,ordertime,starttime,endtime,ordertype,mileage,driverid,vehicleid, plateno,companyid,passengerphone,passengers,userid,paymentstatus,shouldpayamount orderamount,orderstatus,departuretime,arrivaltime, pricemodelname cartype ,'' organid, ordersource ,'2' usetype from op_order WHERE orderstatus in ('7','8') and status = '1' UNION ALL select orderno,onaddress,offaddress,estimatedtime,estimatedmileage,undertime,usetime,ordertime,starttime,endtime,ordertype,mileage,driverid,vehicleid, plateno,companyid,passengerphone,passengers,userid,paymentstatus,shouldpayamount orderamount,orderstatus,departuretime,arrivaltime, '' cartype,''organid, ordersource,'2' usetype from op_taxiorder WHERE orderstatus in ('7','8') and status = '1')aa LEFT JOIN pub_driver d on aa.driverid = d.id where 1=1
and STR_TO_DATE(aa.usetime,'%Y-%m-%d') >= STR_TO_DATE(#{starttime},'%Y-%m-%d')
and STR_TO_DATE(aa.usetime,'%Y-%m-%d') <= STR_TO_DATE(#{endtime},'%Y-%m-%d')
and aa.ordertype = #{ordertype}
and aa.usetype = #{usetype}
and aa.userid = #{passengers}
and aa.driverid = #{driver}
and aa.paymentstatus = #{paymentstatus}
and aa.organid = #{organid}
ORDER BY aa.usetime DESC,aa.paymentstatus,ordertype)t LIMIT ${iDisplayStart},${iDisplayLength}

select count(*) from(select * from(select orderno,onaddress,offaddress,estimatedtime,estimatedmileage,undertime,usetime,ordertime,starttime,endtime,ordertype,mileage,driverid, plateno,companyid,passengerphone,passengers,userid,paymentstatus,orderamount,orderstatus,usetype,organid from org_order WHERE orderstatus in ('7','8') and status = '1' UNION ALL select orderno,onaddress,offaddress,estimatedtime,estimatedmileage,undertime,usetime,ordertime,starttime,endtime,ordertype,mileage,driverid, plateno,companyid,passengerphone,passengers,userid,paymentstatus,orderamount,orderstatus,'2'usetype,''organid from op_order WHERE orderstatus in ('7','8') and status = '1' UNION ALL select orderno,onaddress,offaddress,estimatedtime,estimatedmileage,undertime,usetime,ordertime,starttime,endtime,ordertype,mileage,driverid, plateno,companyid,passengerphone,passengers,userid,paymentstatus,orderamount,orderstatus,'2'usetype,''organid from op_taxiorder WHERE orderstatus in ('7','8') and status = '1')aa LEFT JOIN pub_driver d on aa.driverid = d.id where 1=1
and STR_TO_DATE(aa.usetime,'%Y-%m-%d') >= STR_TO_DATE(#{starttime},'%Y-%m-%d')
and STR_TO_DATE(aa.usetime,'%Y-%m-%d') <= STR_TO_DATE(#{endtime},'%Y-%m-%d')
and aa.ordertype = #{ordertype}
and aa.usetype = #{usetype}
and aa.userid = #{passengers}
and aa.driverid = #{driver}
and aa.paymentstatus = #{paymentstatus}
and aa.organid = #{organid}
ORDER BY aa.usetime,aa.paymentstatus,ordertype)t

