paymentstatus,shouldpayamount orderamount,orderstatus,departuretime,arrivaltime, pricemodelname cartype,'' organid, ordersource ,'2' usetype from op_order WHERE orderstatus in ('7','8') and status = '1' UNION ALL select orderno,onaddress,offaddress,estimatedtime,estimatedmileage,undertime,usetime,ordertime,starttime,endtime,ordertype,mileage,driverid,vehicleid, plateno,companyid,passengerphone,passengers,userid,paymentstatus,shouldpayamount orderamount,orderstatus,departuretime,arrivaltime, '' cartype,''organid, ordersource,'2' usetype from op_taxiorder WHERE orderstatus in ('7','8') and status = '1')aa LEFT JOIN pub_driver d on aa.driverid = d.id where 1=1
and STR_TO_DATE(aa.usetime,'%Y-%m-%d') >= STR_TO_DATE(#{starttime},'%Y-%m-%d')
