select * from (select pub_driver.id driverid, pub_driver.name drivername, pub_driver.workstatus, pub_driver.lat, pub_driver.lng, pub_driver.phone, pub_driver.headportraitmin, pub_driver.headportraitmax, op_vehiclemodels.id modelsid, op_vehiclemodels.name modelname, pub_driver.leasescompanyid, pub_vehicle.id vehicleid, concat(pub_vehcbrand.name, " ", pub_vehcline.name) brandname, concat( (select pub_dictionary.text from pub_dictionary where pub_vehicle.platenoprovince = pub_dictionary.value and pub_dictionary.type = "车牌省"), (select pub_dictionary.text from pub_dictionary where pub_vehicle.platenocity = pub_dictionary.value and pub_dictionary.type = "车牌市"), pub_vehicle.plateno ) plateno from pub_driver left join pub_driver_vehicle_ref on pub_driver.id = pub_driver_vehicle_ref.driverid left join pub_vehicle on pub_driver_vehicle_ref.vehicleid = pub_vehicle.id left join op_vehiclemodels_vehicle_ref on pub_vehicle.id = op_vehiclemodels_vehicle_ref.vehicleid left join op_vehiclemodels on op_vehiclemodels_vehicle_ref.vehiclemodelsid = op_vehiclemodels.id left join pub_vehicle_scope on pub_vehicle.id = pub_vehicle_scope.vehicleid left join pub_vehcline on pub_vehicle.vehclineid = pub_vehcline.id left join pub_vehcbrand on pub_vehcline.vehcBrandID = pub_vehcbrand.id where pub_driver.status = 1 and pub_driver_vehicle_ref.status = 1 and pub_vehicle.status = 1 and op_vehiclemodels_vehicle_ref.status = 1 and op_vehiclemodels.status = 1 and pub_vehicle_scope.status = 1 and pub_vehcline.status = 1 and pub_vehcbrand.status = 1 and pub_driver.workstatus in ("0", "2") and pub_driver.platformtype = "1"
union
select pub_driver.id driverid, pub_driver.name drivername, pub_driver.workstatus, pub_driver.lat, pub_driver.lng, pub_driver.phone, pub_driver.headportraitmin, pub_driver.headportraitmax, op_vehiclemodels.id modelsid, op_vehiclemodels.name modelname, pub_driver.leasescompanyid, pub_vehicle.id vehicleid, concat(pub_vehcbrand.name, " ", pub_vehcline.name) brandname, concat( (select pub_dictionary.text from pub_dictionary where pub_vehicle.platenoprovince = pub_dictionary.value and pub_dictionary.type = "车牌省"), (select pub_dictionary.text from pub_dictionary where pub_vehicle.platenocity = pub_dictionary.value and pub_dictionary.type = "车牌市"), pub_vehicle.plateno ) plateno from pub_driver left join pub_driver_vehicle_ref on pub_driver.id = pub_driver_vehicle_ref.driverid left join pub_vehicle on pub_driver_vehicle_ref.vehicleid = pub_vehicle.id left join pub_vehcline on pub_vehicle.vehclineid = pub_vehcline.id left join pub_vehcbrand on pub_vehcline.vehcBrandID = pub_vehcbrand.id left join op_vehcline_models_ref on pub_vehcline.id = op_vehcline_models_ref.vehclineid left join op_vehiclemodels on op_vehcline_models_ref.vehiclemodelsid = op_vehiclemodels.id left join pub_vehicle_scope on pub_vehicle.id = pub_vehicle_scope.vehicleid where pub_driver.status = 1 and pub_driver_vehicle_ref.status = 1 and pub_vehicle.status = 1 and pub_vehcbrand.status = 1 and op_vehcline_models_ref.status = 1 and op_vehiclemodels.status = 1 and pub_vehicle_scope.status = 1 and pub_driver.workstatus in ("0", "2") and pub_driver.platformtype = "0" ) driverTmp limit #{iDisplayStart}, #{iDisplayLength}

