mapper.select:
SELECT fav.*,CONCAT(fav.name,fav.phone) namephone FROM( ( SELECT pu.id, pu.id userid, pu.nickname `name`, pu.account phone, pu.createtime, pu.updatetime, pu.id creater, pu.id updater, pu.`Status` FROM pe_user pu WHERE 1=1 AND pu.`status` = 1 AND pu.id = #{userid} ) UNION ( SELECT pm.* FROM pub_mostcontact pm, pe_user pu WHERE 1=1 AND pu.`status` = 1 AND pm.`status` = 1 AND pm.userphone = pu.account AND pm.phone != pu.account AND pu.id = #{userid} ) ) fav
WHERE (fav.phone LIKE '%${sSearch}%' OR fav.name LIKE '%${sSearch}%')
GROUP BY CONCAT(fav.name,fav.phone) LIMIT ${iDisplayStart},${iDisplayLength}

SELECT pm.*,pca.city citycaption FROM pub_mostaddress pm, pub_cityaddr pca WHERE 1=1 AND pca.`status` = 1 AND pm.`status` = 1 AND pca.id = pm.city AND pm.userphone = #{userid}
AND ( pca.city LIKE '%${sSearch}%' OR pm.address LIKE '%${sSearch}%' )
LIMIT ${iDisplayStart},${iDisplayLength}

select * from pub_cityaddr where id in ( select city from op_accountrules where rulestype = #{ordertype} and rulesstate = "0" and status = 1 ) and id in ( select city from pub_sendrules where platformtype = "0" and vehicletype = 0 and status = 1 ) and status = 1
and id = #{city}
order by fullnameinitials

select pub_provinceaddr.id provinceid, pub_provinceaddr.city provincecity, pub_provinceaddr.markid provincemarkid, pub_cityaddr.id cityid, pub_cityaddr.city city, pub_cityaddr.markid citymarkid from pub_cityaddr left join pub_provinceaddr on pub_cityaddr.provinceid = pub_provinceaddr.id where pub_cityaddr.status = 1 and pub_cityaddr.id in ( select city from op_taxiaccountrules where rulesstate = "0" and status = 1 ) and pub_cityaddr.id in ( select city from op_taxisendrules where rulesstate = "0" and status = 1 )
and pub_cityaddr.id = #{city}
order by pub_provinceaddr.sortno desc, pub_provinceaddr.cityinitials, pub_cityaddr.sortno desc, pub_cityaddr.cityinitials
