mapper.select:
SELECT t1.*,t2.`name` AS companyName,t3.nickname AS operator FROM pub_cooagreement t1 LEFT JOIN le_leasescompany t2 ON (t1.leasecompanyid = t2.id AND t2.`Status`=1) LEFT JOIN op_user t3 ON (t1.updater = t3.id AND t3.`Status`=1) WHERE 1=1 AND t1.`status`=1 AND t1.platformtype=0 AND t1.companyid = #{key}
AND t2.`name` like "%"#{queryCompanyname}"%"
AND t1.servicetype = #{queryServicetype}
ORDER BY t1.updatetime DESC LIMIT #{iDisplayStart},#{iDisplayLength}

SELECT count(*) AS companyName FROM pub_cooagreement t1 LEFT JOIN le_leasescompany t2 ON (t1.leasecompanyid = t2.id AND t2.`Status`=1) LEFT JOIN op_user t3 ON (t1.updater = t3.id AND t3.`Status`=1) WHERE 1=1 AND t1.`status`=1 AND t1.platformtype=0 AND t1.companyid = #{key}
AND t2.`name` like "%"#{queryCompanyname}"%"
AND t1.servicetype = #{queryServicetype}
