			updatetime = now()

				, reviewstatus = #{reviewstatus} 


				, orderreason = #{orderreason}


				, reviewperson = #{reviewperson}


				, shouldpayamount = #{shouldpayamount}


				, actualpayamount = #{actualpayamount}


				, orderamount = #{orderamount}


				, paymentstatus = #{paymentstatus}


				, vehicleid = #{vehicleid}


				, orderstatus = #{orderstatus}


				, ordersortcolumn = #{ordersortcolumn}


				, companyid = #{companyid}


				, driverid = #{driverid}


				, plateno = #{plateno}


				, vehcbrandname = #{vehcbrandname}


				, vehclinename = #{vehclinename}


				, ordertime = #{ordertime}


				, cancelparty = #{cancelparty}


				, canceltime = #{canceltime}


                , belongleasecompany = #{belongleasecompany}

		where
			orderno = #{orderno}

mapper.select:
select pub_vehicle.id, pub_vehicle.driverid, pub_driver.workstatus, pub_driver.lat, pub_driver.lng, pub_driver.phone, pub_driver.headportraitmin, pub_driver.headportraitmax, pub_vehicle.leasescompanyid, pub_driver.name, pub_driver.phone, concat( (select pub_dictionary.text from pub_dictionary where pub_vehicle.platenoprovince = pub_dictionary.value and pub_dictionary.type = "车牌省"), (select pub_dictionary.text from pub_dictionary where pub_vehicle.platenocity = pub_dictionary.value and pub_dictionary.type = "车牌市"), pub_vehicle.plateno ) plateno from pub_vehicle left join pub_driver on pub_vehicle.driverid = pub_driver.id left join pub_vehicle_scope on pub_vehicle.id = pub_vehicle_scope.vehicleid left join le_leasescompany on pub_vehicle.leasescompanyid = le_leasescompany.id and le_leasescompany.tocstate = '2' where pub_driver.jobstatus = '0' and pub_driver.workstatus in ("0", "1", "2") and pub_driver.vehicletype = '1' and pub_driver.boundstate = 1 and pub_vehicle.vehicletype = '1' and pub_vehicle.vehiclestatus = '0' and pub_vehicle.boundstate = '1' and (pub_vehicle_scope.cityid = #{oncity} or pub_vehicle_scope.cityid = #{offcity}) and pub_driver.phone not in (select account from pe_user where pe_user.id = #{userId} and pe_user.status = 1) and pub_driver.passworkstatus != "3" and pub_driver.passworkstatus != "4"
and pub_driver.id not in (select pub_driver.id from op_taxiorder left join pub_driver on op_taxiorder.driverid = pub_driver.id where op_taxiorder.isusenow = 1 and op_taxiorder.orderstatus in ('2', '3', '4', '6')) and pub_driver.id not in (select pub_driver.id from op_taxiorder left join pub_driver on op_taxiorder.driverid = pub_driver.id where op_taxiorder.isusenow = 0 and op_taxiorder.orderstatus in ('2', '3', '4', '6')
and op_taxiorder.usetime <= date_format(#{estimatedEndtime}, '%Y-%m-%d %H:%i') )
and pub_driver.id not in (select pub_driver.id from op_taxiorder left join pub_driver on op_taxiorder.driverid = pub_driver.id where op_taxiorder.isusenow = 1 and op_taxiorder.orderstatus in ('2', '3', '4', '6')
and date_add(op_taxiorder.usetime, interval (1) hour) <= #{usetime} ) and pub_driver.id not in (select pub_driver.id from op_taxiorder left join pub_driver on op_taxiorder.driverid = pub_driver.id where op_taxiorder.isusenow = 0 and op_taxiorder.orderstatus in ('2', '3', '4', '6')
and date_format(op_taxiorder.usetime, '%Y-%m-%d') = date_format(#{usetime}, '%Y-%m-%d') )
and pub_driver.workstatus = #{driverState}
and pub_driver.lat >= ${minLat} and pub_driver.lat <= ${maxLat}
and pub_driver.lng >= ${minLon} and pub_driver.lng <= ${maxLon}
and pub_vehicle.id = #{vehicleid}
and pub_driver.id != #{driverid}
and pub_driver.lng > 0 and pub_driver.lat > 0 group by pub_vehicle.id limit #{iDisplayStart}, #{iDisplayLength}

select count(*) from (
	select
		pub_vehicle.id
	from
		pub_vehicle left join pub_driver on pub_vehicle.driverid = pub_driver.id
		left join pub_vehicle_scope on pub_vehicle.id = pub_vehicle_scope.vehicleid
		left join le_leasescompany on pub_vehicle.leasescompanyid = le_leasescompany.id and le_leasescompany.tocstate = '2'
	where
		pub_driver.jobstatus = '0' and pub_driver.workstatus = '0' and pub_driver.vehicletype = '1' and pub_driver.boundstate = 1
		and pub_vehicle.vehicletype = '1' and pub_vehicle.vehiclestatus = '0' and pub_vehicle.boundstate = '1'
		and (pub_vehicle_scope.cityid = #{oncity} or pub_vehicle_scope.cityid = #{offcity})
		and pub_driver.phone not in (select account from pe_user where pe_user.id = #{userId} and pe_user.status = 1)
		and pub_driver.passworkstatus != "3" and pub_driver.passworkstatus != "4"

			and pub_driver.id not in (select pub_driver.id from op_taxiorder left join pub_driver on op_taxiorder.driverid = pub_driver.id where op_taxiorder.isusenow = 1 and op_taxiorder.orderstatus in ('2', '3', '4', '6'))
			and pub_driver.id not in (select pub_driver.id from op_taxiorder left join pub_driver on op_taxiorder.driverid = pub_driver.id where op_taxiorder.isusenow = 0 and op_taxiorder.orderstatus in ('2', '3', '4', '6')

				and op_taxiorder.usetime <= date_format(#{estimatedEndtime}, '%Y-%m-%d %H:%i')
			)


			and pub_driver.id not in (select pub_driver.id from op_taxiorder left join pub_driver on op_taxiorder.driverid = pub_driver.id where op_taxiorder.isusenow = 1 and op_taxiorder.orderstatus in ('2', '3', '4', '6')

				and date_add(op_taxiorder.usetime, interval (1) hour) <= #{usetime}
			)
			and pub_driver.id not in (select pub_driver.id from op_taxiorder left join pub_driver on op_taxiorder.driverid = pub_driver.id where op_taxiorder.isusenow = 0 and op_taxiorder.orderstatus in ('2', '3', '4', '6')

				and date_format(op_taxiorder.usetime, '%Y-%m-%d') = date_format(#{usetime}, '%Y-%m-%d')
			)


			and pub_driver.workstatus = #{driverState}



				and pub_driver.lat >= ${minLat} and pub_driver.lat <= ${maxLat}




				and pub_driver.lng >= ${minLon} and pub_driver.lng <= ${maxLon}



			and pub_vehicle.id = #{vehicleid}


			and pub_driver.id != #{driverid}

		and pub_driver.lng > 0 and pub_driver.lat > 0
	group by
		pub_vehicle.id
) temp
