mapper.select:

select actualbalance
from org_organ_company_ref
where organid = #{organId}
  and companyid = #{leasesCompanyId}
  and status = 1


select actualbalance,IFNULL(couponamount,0) as couponamount
from org_organ_company_ref
where organid = #{organId}
  and companyid = #{leasesCompanyId}
  and status = 1

mapper.update:
	update org_organ_company_ref
	set actualbalance=IFNULL(actualbalance,0) - #{money},updatetime=now(),couponamount=IFNULL(couponamount,0) - #{couponvalue}
	where organid = #{organId}
      and companyid = #{leasesCompanyId}
      and status = 1

mapper.insert:
	    insert into org_organexpenses(id,organid,leasescompanyid,type,expensetime,amount,balance,remark,createtime,updatetime,creater,updater,status,operatortype,operateresult)
        select #{id},#{organId},#{leasesCompanyId},#{type},now(),#{amount},IFNULL(actualbalance,0),#{remark},now(),now(),#{creater},#{updater},1,#{operatorType},#{operateResult} 
        from org_organ_company_ref 
        where organid = #{organId} 
          and companyid = #{leasesCompanyId} 
          and status = 1

mapper.select:
select org_organ_company_ref.*
from org_organ_company_ref
where org_organ_company_ref.organid = #{organId} 
  and org_organ_company_ref.companyid = #{leasesCompanyId}
  and org_organ_company_ref.status = 1

mapper.update:
	update org_organ_company_ref
	set lineofcredit = #{lineOfCredit}, balance = #{balance} , updatetime = now()
	where org_organ_company_ref.organid = #{organId} 
      and org_organ_company_ref.companyid = #{leasesCompanyId}
      and org_organ_company_ref.status = 1

		update org_organcreditrecord
		set effectivetime = now(),updatetime= now()
        where organid = #{organId}
          and leasescompanyid = #{leasesCompanyId}
          and oldcredit = #{oldCredit}
          and currentcredit = #{currentCredit}
        order by changetime desc limit 1

	update org_organ_company_ref
	set actualbalance = IFNULL(actualbalance,0) + #{rechargeAmount}, balance = IFNULL(balance,0) + #{rechargeAmount} , updatetime = now()
	where org_organ_company_ref.organid = #{organId}
      and org_organ_company_ref.companyid = #{companyId}
      and org_organ_company_ref.status = 1

mapper.select:
select * from org_organpaymentrecord
where outtradeno = #{outtradeno}
  and status = 1

mapper.update:
update org_organ_company_ref
set actualbalance=IFNULL(actualbalance,0) - #{amount},balance=IFNULL(balance,0) - #{amount},updatetime=now()
where organid = #{organId}
  and companyid = #{leasesCompanyId}
  and status = 1

mapper.select:
SELECT * FROM pub_coupon_detail WHERE `status`=1 AND userid IN ( select organ.id from org_organ organ where organ.status = 1 and organ.creditcode = ( select org_organ.creditcode from org_organ where org_organ.status = 1 and org_organ.id = #{organId} ) and organ.customertype!=1 )
and createtime >= STR_TO_DATE(#{startTime},'%Y-%m-%d %H:%i:%s')
and createtime < STR_TO_DATE(ADDDATE(#{endTime},1),'%Y-%m-%d %H:%i:%s')
and usetype = #{type}
ORDER BY createtime DESC LIMIT #{iDisplayStart},#{iDisplayLength}

mapper.insert:
	    insert into pub_coupon_detail(id,userid,couponidref,usetype,amount,balance,remark,platformtype,lecompanyid,updatetime,createtime,status)
	    select #{id},#{organId},#{couponidref},#{usetype},#{couponvalue},couponamount,#{remark},1,#{leasesCompanyId},now(),now(),1
	    from org_organ_company_ref 
        where organid = #{organId} 
          and companyid = #{leasesCompanyId} 
          and status = 1

mapper.update:
update org_organ_company_ref
set balance=IFNULL(balance,0) + #{couponvalue},couponamount=IFNULL(couponamount,0) + #{couponvalue},updatetime=now()
where organid = #{organId}
  and companyid = #{leasesCompanyId}
  and status = 1
