mapper.select:
SELECT t.* FROM ( SELECT (@rownum := @rownum + 1) AS indexnum, m.* FROM ( SELECT org_order.orderno, org_order.companyid, org_user.nickname, org_user.account, org_order.passengers, org_order.passengerphone,
CONCAT( org_order.passengers," ",org_order.passengerphone ) AS userMessage, le_leasescompany.shortname AS companyName, org_order.ordertype, org_order.usetime, org_order.orderstatus, org_order.paymentstatus, org_order.reviewstatus, org_order.paymethod, org_order.orderamount, org_order.expensetype, t.cancelamount, t.cancelnature FROM org_order LEFT JOIN org_ordercancel as t ON (org_order.orderno = t.orderno and t.`status` =1), org_user, le_leasescompany, (SELECT @rownum := 0) r WHERE org_order.`status` = 1 AND org_user.`Status` = 1 AND le_leasescompany.`Status` = 1 AND org_order.userid = org_user.id AND org_order.companyid = le_leasescompany.id AND org_order.paymethod = '2'
AND org_user.id = #{key}
) m where 1=1
and (m.passengers like "%"#{queryUserMessage}"%" or m.passengerphone like "%"#{queryUserMessage}"%")
and m.usetime >= #{startTime} and m.usetime < #{endTime}
and m.usetime >= #{startTime}
and m.usetime < #{endTime}
and m.orderstatus in
#{item}
and m.orderstatus = '7' and m.paymentstatus = '0'
and m.orderstatus = '7' and m.paymentstatus = '1'
and (m.orderstatus = '7' and m.paymentstatus = '2') or (m.expensetype = 2 and m.cancelnature = 1 and m.paymentstatus = '2')
and (m.orderstatus = '7' and m.paymentstatus = '3') or (m.expensetype = 2 and m.cancelnature = 1 and m.paymentstatus = '3')
and (m.orderstatus = '7' and m.paymentstatus = '4') or (m.expensetype = 2 and m.cancelnature = 1 and m.paymentstatus = '4')
and m.orderstatus = '8' and m.paymentstatus = '9'
and m.ordertype = #{queryVehicleMode}
and m.paymethod = #{queryPaymentMethod}
and m.expensetype = #{queryExpensetype}
and m.expensetype = #{queryExpensetype} and m.paymentstatus in (2,3,4) and m.cancelnature = 1
ORDER BY m.usetime DESC ) t WHERE
t.indexnum > #{iDisplayStart} and t.indexnum <= (#{iDisplayStart} + #{iDisplayLength})

SELECT count(*) FROM ( SELECT (@rownum := @rownum + 1) AS indexnum, m.* FROM ( SELECT org_order.orderno, org_order.companyid, org_user.nickname, org_user.account, org_order.passengers, org_order.passengerphone,
CONCAT( org_order.passengers," ",org_order.passengerphone ) AS userMessage, le_leasescompany.shortname AS queryCompany, org_order.ordertype, org_order.usetime, org_order.orderstatus, org_order.paymentstatus, org_order.reviewstatus, org_order.paymethod, org_order.orderamount, org_order.expensetype, t.cancelamount, t.cancelnature FROM org_order LEFT JOIN org_ordercancel as t ON (org_order.orderno = t.orderno and t.`status` =1), org_user, le_leasescompany, (SELECT @rownum := 0) r WHERE org_order.`status` = 1 AND org_user.`Status` = 1 AND le_leasescompany.`Status` = 1 AND org_order.userid = org_user.id AND org_order.companyid = le_leasescompany.id AND org_order.paymethod = '2'
AND org_user.id = #{key}
) m where 1=1
and (m.passengers like "%"#{queryUserMessage}"%" or m.passengerphone like "%"#{queryUserMessage}"%")
and m.usetime >= #{startTime} and m.usetime <= #{endTime}
and m.usetime >= #{startTime}
and m.usetime <= #{endTime}
and m.orderstatus in
#{item}
and m.orderstatus = '7' and m.paymentstatus = '0'
and m.orderstatus = '7' and m.paymentstatus = '1'
and (m.orderstatus = '7' and m.paymentstatus = '2') or (m.expensetype = 2 and m.cancelnature = 1 and m.paymentstatus = '2')
and (m.orderstatus = '7' and m.paymentstatus = '3') or (m.expensetype = 2 and m.cancelnature = 1 and m.paymentstatus = '3')
and (m.orderstatus = '7' and m.paymentstatus = '4') or (m.expensetype = 2 and m.cancelnature = 1 and m.paymentstatus = '4')
and m.orderstatus = '8' and m.paymentstatus = '9'
and m.ordertype = #{queryVehicleMode}
and m.paymethod = #{queryPaymentMethod}
and m.expensetype = #{queryExpensetype}
and m.expensetype = #{queryExpensetype} and m.paymentstatus in (2,3,4) and m.cancelnature = 1
ORDER BY m.usetime DESC ) t
