mapper.select:
select * from pub_cityaddr where id in ( select le_accountrules.city from le_accountrules left join le_company_rules_ref on le_accountrules.rulesrefid = le_company_rules_ref.id left join org_organ on le_company_rules_ref.organid = org_organ.id left join org_organ_company_ref on org_organ.id = org_organ_company_ref.organid where le_company_rules_ref.leasescompanyid = #{companyid} and org_organ_company_ref.companyid = #{companyid} and org_organ.creditcode = (select org_organ.creditcode from org_user left join org_organ on org_user.organid = org_organ.id where org_user.id = #{userid}) and org_organ.customertype!=1 and le_company_rules_ref.rulestate = '1' and le_accountrules.leasescompanyid = #{companyid} and le_accountrules.rulestype = #{ordertype} and le_accountrules.type = '1' and le_accountrules.cartype in ( select org_usecarrules.vehiclemodels from org_usecarrules left join org_user_rules_ref on org_usecarrules.id = org_user_rules_ref.userulesid and org_user_rules_ref.userid = #{userid} and org_usecarrules.usetype = #{ordertype} and org_usecarrules.leasescompanyid = #{companyid} and org_usecarrules.status = 1 and org_user_rules_ref.status = 1 ) and le_accountrules.status = 1 and le_accountrules.status = 1 and le_company_rules_ref.status = 1 and org_organ.status = 1 and org_organ_company_ref.status = 1 ) and id in ( select city from pub_sendrules where leasescompanyid = #{companyid} and platformtype = "1" and vehicletype = 0 and status = 1 ) and status = 1
and id = #{city}
order by fullnameinitials
