mapper.select:
SELECT count(*) FROM org_dept WHERE org_dept. STATUS = 1
AND org_dept.parentid = #{parentid}
AND ( org_dept.parentid IS NULL OR org_dept.parentid = '' )
AND org_dept.organid in ( SELECT organ.id FROM org_organ organ WHERE organ.status = 1 AND organ.creditcode = ( SELECT org_organ.creditcode FROM org_organ WHERE org_organ. STATUS = 1 AND org_organ.id = #{organid} ) AND organ.customertype!=1 ) AND org_dept.deptname = #{deptname} AND org_dept.id != #{deptid}

mapper.update:
UPDATE org_dept
SET org_dept. STATUS = 0,
 org_dept.updatetime = now(),
 org_dept.updater = #{updater}
WHERE
	org_dept.id = #{deptid}

update pe_user
	set pe_user.userpassword = #{password}
where
	pe_user.status = 1
and pe_user.account=#{account}

update org_user
	set org_user.userpassword = #{password}
where
	org_user.status = 1
and org_user.account=#{account}

UPDATE org_organ_company_ref
SET org_organ_company_ref.firsttime = '1',
 org_organ_company_ref.UpdateTime = now()
WHERE
	org_organ_company_ref.userid = #{id}
AND org_organ_company_ref.organid = #{organId}
