mapper.select:
SELECT org_order.*, (SELECT le_vehiclemodels.name from le_vehiclemodels where le_vehiclemodels.status=1 and le_vehiclemodels.id=org_order.pricemodel) as selectedmodelcaption, (select le_vehiclemodels.logo from le_vehiclemodels where le_vehiclemodels.status=1 and le_vehiclemodels.leasescompanyid = org_order.companyid and le_vehiclemodels.id=org_order.pricemodel) as selectedmodellogo FROM org_order, org_user WHERE org_user.id = org_order.userid AND org_order.status = 1
AND org_order.paymethod != '2'
AND org_order.orderstatus = #{orderstatus}
AND org_order.paymentstatus=#{paymentstatus}
AND org_user.account = #{account} AND org_user.customertype!=1
AND org_order.companyid = #{companyid}
ORDER BY org_order.updatetime LIMIT 1

SELECT op_order.*, (SELECT op_vehiclemodels.name from op_vehiclemodels where op_vehiclemodels.status=1 and op_vehiclemodels.id=op_order.pricemodel) as selectedmodelcaption, (SELECT op_vehiclemodels.logo from op_vehiclemodels where op_vehiclemodels.status=1 and op_vehiclemodels.id=op_order.pricemodel) as selectedmodellogo FROM op_order, pe_user WHERE pe_user.id = op_order.userid AND pe_user.status = 1 AND op_order.status = 1
AND op_order.orderstatus = #{orderstatus}
AND op_order.paymentstatus=#{paymentstatus}
AND pe_user.account = #{account} ORDER BY op_order.updatetime LIMIT 1

mapper.update:
UPDATE org_order SET paymentstatus = #{paymentstatus}, paytype=#{paytype}, completetime = now(), updatetime = now() WHERE org_order.status = 1
AND org_order.orderno = ( SELECT orderno FROM org_orderpaymentrecord WHERE org_orderpaymentrecord.status = 1 AND org_orderpaymentrecord.outtradeno = #{outtradeno} )
AND org_order.orderno = #{orderno}

UPDATE op_order SET paymentstatus = #{paymentstatus}, paytype=#{paytype}, completetime = now(), updatetime = now() WHERE op_order.status = 1
AND op_order.orderno = ( SELECT orderno FROM op_orderpaymentrecord WHERE op_orderpaymentrecord.status = 1 AND op_orderpaymentrecord.outtradeno = #{outtradeno} )
AND op_order.orderno = #{orderno}

mapper.select:
SELECT op_taxisendrules.* FROM op_taxisendrules, pub_cityaddr WHERE op_taxisendrules.STATUS = 1 AND pub_cityaddr.STATUS = 1 AND pub_cityaddr.id = op_taxisendrules.city
AND op_taxisendrules.usetype='0' AND op_taxisendrules.rulesstate='0'
AND pub_cityaddr.city=#{city}
